INSERT INTO `news` (`nid`, `title`, `poster`, `content`, `created_at`) VALUES
(1, 'Đường phèn', 'duongphen.jpg', '<p>Để uống tr&agrave;, tuỳ khẩu vị mọi người c&oacute; thể th&ecirc;m đường, đường ph&egrave;n, mật ong, chanh...</p>\r\n\r\n<p>Đường ph&egrave;n kh&aacute;c với đường k&iacute;nh trắng, l&agrave; một vị thuốc trong Đ&ocirc;ng y, c&oacute; rất nhiều lợi &iacute;ch, n&ecirc;n h&ocirc;m nay m&igrave;nh chia sẻ một ch&uacute;t th&ocirc;ng tin về đường ph&egrave;n.&nbsp;</p>\r\n\r\n<p>&bull; Đường ph&egrave;n (t&ecirc;n khoa học Saccharose) c&ograve;n gọi l&agrave; băng đường. Cũng giống như đường c&aacute;t, đường ph&egrave;n được l&agrave;m từ nước m&iacute;a, nước củ cải đường v&agrave; một số nguy&ecirc;n liệu kh&aacute;c như thốt nốt, l&uacute;a miến ngọt&hellip;. Đường ph&egrave;n c&oacute; chứa saccharose v&agrave; một số nguy&ecirc;n tố vi lượng gi&uacute;p ph&acirc;n giải th&agrave;nh glucose v&agrave; fructose.</p>\r\n\r\n<p>&bull; Đường ph&egrave;n được b&agrave;o chế bằng c&aacute;ch lấy lấy đường trắng, h&ograve;a lo&atilde;ng với nước ở một lượng nhất định sau đ&oacute; cho v&ocirc;i, trứng g&agrave; v&agrave;o lọc để l&agrave;m dịu vị ngọt, lọc tạo chất v&agrave; th&ecirc;m hương vị. Đem đun hỗn hợp n&agrave;y l&ecirc;n, giữ lửa đều, nước gần cạn th&igrave; đổ th&ecirc;m nước v&agrave;o đun tiếp. Khi n&agrave;o đường ch&iacute;n tới th&igrave; đổ v&agrave;o th&ugrave;ng, b&ecirc;n trong c&oacute; vỉ tre. Sau 10-12 ng&agrave;y sẽ c&oacute; đường ph&egrave;n kết tinh th&agrave;nh từng khối như ta vẫn mua ngo&agrave;i thị trường.</p>\r\n\r\n<p>&bull; Theo Đ&ocirc;ng y, đường ph&egrave;n vị ngọt t&iacute;nh b&igrave;nh, v&agrave;o tỳ v&agrave; phế.<br />\r\nC&ocirc;ng dụng ch&iacute;nh của đường ph&egrave;n l&agrave;: bổ trung &iacute;ch kh&iacute;, h&ograve;a vị nhuận phế, chỉ kh&aacute;i trừ đ&agrave;m.<br />\r\nĐường ph&egrave;n được sử dụng l&agrave;m gia vị để khai vị trợ ti&ecirc;u h&oacute;a. D&ugrave;ng cho c&aacute;c trường hợp vi&ecirc;m kh&iacute; phế quản, ho khan &iacute;t đờm, đau r&aacute;t họng, kh&iacute; huyết hư, ch&oacute;ng mặt, đau đầu.</p>\r\n\r\n<p>&bull; Đường ph&egrave;n c&oacute; t&aacute;c dụng thanh nhiệt tốt n&ecirc;n v&agrave;o mu&agrave; nắng n&oacute;ng, sau giờ lao động, mọi người thường ngậm một miếng đường ph&egrave;n để nhanh ch&oacute;ng giải nhiệt. V&igrave; thế mới c&oacute; c&acirc;u &ldquo;ngọt như đường c&aacute;t, m&aacute;t như đường ph&egrave;n&rdquo;.</p>\r\n\r\n<p>&bull; Đường ph&egrave;n được sử dụng trong nhiều b&agrave;i thuốc trị ho như:<br />\r\n- Chưng với hoa c&uacute;c c&oacute; t&aacute;c dụng hạ huyết &aacute;p.<br />\r\n- Chưng c&aacute;ch thủy với hoa điệp, phơi sương rồi uống v&agrave;o l&uacute;c s&aacute;ng sớm để trị ho l&acirc;u ng&agrave;y, trị vi&ecirc;m họng, d&ugrave;ng cho người lao phổi.<br />\r\n- Chưng với c&aacute;nh hoa hồng c&ograve;n tươi để uống trị ho do thời tiết.<br />\r\n- Nấu đặc với bầu, gạn bỏ b&atilde; lấy nước d&ugrave;ng c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, gi&uacute;p ăn uống ngon miệng hơn.<br />\r\n- Nấu với vỏ qu&yacute;t để trị chứng ho khan do thời tiết g&acirc;y ra.<br />\r\n- Pha c&ugrave;ng gừng tươi với nước s&ocirc;i để trị cảm dao thay đổi thời tiết.<br />\r\n- Nấu chung với t&aacute;o tầu, gừng tươi để trị cảm ho, vi&ecirc;m đường h&ocirc; hấp do thời tiết.<br />\r\n- Nấu ch&aacute;o với gạo nếp, nh&acirc;n s&acirc;m, hạt sen để bồi bổ kh&iacute;<br />\r\nhuyết.</p>\r\n', '2016-11-28 16:38:09'),
(2, 'Bí đao', 'bidao.jpg', '<p>Bạn thừa c&acirc;n, bạn lo lắng về đ&aacute;m mỡ ở bụng, ở đ&ugrave;i..., bạn đ&atilde; cố nhịn ăn, tập thể dục đến mệt nhừ người...m&agrave; vẫn kh&ocirc;ng ưng &yacute;?</p>\r\n\r\n<p>B&iacute; đao chư&aacute; nhiều chất xơ, kh&ocirc;ng chứa chất b&eacute;o. Hơn nữa trong b&iacute; đao c&ograve;n chứa hợp chất Hyterin-caperin ngăn kh&ocirc;ng cho đường chuyển ho&aacute; th&agrave;nh mỡ trong cơ thể, n&ecirc;n cơ thể sẽ kh&ocirc;ng bị t&iacute;ch luỹ mỡ thừa.</p>\r\n\r\n<p>Ngo&agrave;i ra trong b&iacute; đao chứa nhiều c&aacute;c vitamin C, vitamin E, v&agrave; c&aacute;c kho&aacute;ng chất Kali, Phospho, Magie&hellip; gi&uacute;p l&agrave;m đẹp cơ thể. B&iacute; đao vốn l&agrave; một phương thuốc l&agrave;m đẹp của c&aacute;c mỹ nh&acirc;n được lưu truyền từ xưa, n&oacute; giữ ẩm cho da, l&agrave;m da căng mịn, s&aacute;ng hồng, bớt dầu v&agrave; mụn đầu đen.</p>\r\n\r\n<p>Chỉ cần cho b&iacute; đao v&agrave;o nước ấm để h&atilde;m lấy nước uống thay nước lọc trong ng&agrave;y. Trong 1 th&aacute;ng bạn sẽ thấy v&oacute;c d&aacute;ng cơ thể được cải thiện r&otilde; rệt!</p>\r\n', '2016-11-28 16:39:52'),
(3, 'Khuyến mại tháng 8!!!', 'khuyenmaithang8.jpg', '<p>GIẢM GI&Aacute; 15% CHO C&Aacute;C BẠN SINH TH&Aacute;NG 8!</p>\r\n\r\n<p>GIẢM GI&Aacute; 10% TẤT CẢ C&Aacute;C ĐƠN H&Agrave;NG!</p>\r\n\r\n<p>&Aacute;p dụng đến hết th&aacute;ng 9</p>\r\n\r\n<p>Cho tất cả c&aacute;c bạn like page v&agrave; mua tr&agrave; ở Quỳnh Phương - Herbal tea</p>\r\n\r\n<p>Mỗi ly tr&agrave; thảo dược của shop đều l&agrave; liều thuốc bổ cho sức khoẻ v&agrave; tinh thần của bạn</p>\r\n\r\n<p>C&ograve;n g&igrave; gi&uacute;p l&agrave;m thư th&aacute;i đầu &oacute;c hơn những ấm tr&agrave; thơm, ngon, đẹp mắt</p>\r\n', '2016-11-28 16:42:34'),
(4, 'Hoa nhài - vị thuốc quý', 'hoanhai.jpg', '<p>Con g&aacute;i ai cũng y&ecirc;u hoa, ai cũng th&iacute;ch được tặng hoa!</p>\r\n\r\n<p>Được tặng hoa rồi lại c&ograve;n quan t&acirc;m đến sức khoẻ nữa th&igrave; hẳn phải ph&aacute;t cuồng l&ecirc;n mất &yacute; &lt;3</p>\r\n\r\n<p>Hoa Nh&agrave;i m&ugrave;i thơm dịu d&agrave;ng, m&agrave;u trắng tinh khiết, đẹp như n&agrave;ng thơ, lại c&oacute; rất rất rất nhiều t&aacute;c dụng đối với sức khoẻ v&agrave; l&agrave;m đẹp.</p>\r\n\r\n<p>Mua tr&agrave; hoa nh&agrave;i tặng những người phụ nữ bạn y&ecirc;u thương đi n&agrave;o</p>\r\n\r\n<p>&bull; T&aacute;c dụng :<br />\r\n+ Loại bỏ căng thẳng v&agrave; trầm cảm.<br />\r\n+ Gi&uacute;p điều trị mất ngủ, cao huyết &aacute;p, hoa mắt nhức đầu.<br />\r\n+ Cải thiện ti&ecirc;u h&oacute;a, ngăn ngừa c&aacute;c bệnh như ti&ecirc;u chảy, vi&ecirc;m lo&eacute;t dạ d&agrave;y.<br />\r\n+ Gi&uacute;p loại bỏ c&aacute;c chất độc.<br />\r\n+ Giảm sự t&iacute;ch tụ Cholesteron xấu, giảm nguy cơ bệnh tim v&agrave; đột quy.<br />\r\n+ Gi&uacute;p điều tiết chuyển h&oacute;a Glucose, hữu &iacute;ch trong ngăn ngừa v&agrave; điều trị tiểu đường.<br />\r\n+ Gi&uacute;p đẩy mạnh qu&aacute; tr&igrave;nh trao đổi chất.<br />\r\n+ Cải thiện lưu th&ocirc;ng m&aacute;u.<br />\r\n+ Trị ho v&agrave; giảm đau, giảm sưng v&agrave; vi&ecirc;m khớp.<br />\r\n+ Gi&uacute;p da căng s&aacute;ng mịn, ngăn ngừa l&atilde;o h&oacute;a</p>\r\n\r\n<p>&bull; Một số c&ocirc;ng thức với hoa Nh&agrave;i:<br />\r\n1. Trị mất ngủ: Hoa Nh&agrave;i 6g, T&acirc;m sen 8g. H&atilde;m với nước s&ocirc;i uống h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>2. Gi&uacute;p thanh nhiệt, tỉnh t&aacute;o: hoa Nh&agrave;i 6g. H&atilde;m với 300ml nước ấm khoảng 5 ph&uacute;t khi tỏa m&ugrave;i thơm l&agrave; d&ugrave;ng được. C&oacute; thể th&ecirc;m mật ong, đường Ph&egrave;n t&ugrave;y khẩu vị.</p>\r\n\r\n<p>3. Trị tăng huyết &aacute;p: Hoa Nh&agrave;i 10g, hoa H&ograve;e 10g, hoa Đại 6g, hoa C&uacute;c 6g. Sắc uống.</p>\r\n\r\n<p>4. Chữa đau đầu ch&oacute;ng mặt: hoa Nh&agrave;i, hoa C&uacute;c đều 6g. H&atilde;m với nước ấm uống thay nước ch&egrave; h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>5. Chữa mụn nhọt: hoa Nh&agrave;i 10g, Kim ng&acirc;n hoa 20g, Bồ c&ocirc;ng anh 20g, Cam thảo 16g. Sắc uống.</p>\r\n', '2016-11-28 16:44:46'),
(5, '"Mắt rồng"', 'longnhan.jpg', '<p>Long nh&atilde;n l&agrave; thực phẩm đ&atilde; qu&aacute; quen thuộc đối với mọi người, c&oacute; thể d&ugrave;ng pha c&ugrave;ng c&agrave;c loại tr&agrave;, hoặc ăn k&egrave;m, hoặc nấu ch&egrave;...<br />\r\n<br />\r\nT&ecirc;n Long nh&atilde;n xuất ph&aacute;t bởi h&igrave;nh dạng giống mắt của con rồng.<br />\r\nS&aacute;ch &ldquo;Thần n&ocirc;ng bản thảo kinh&rdquo; gọi tr&aacute;i nh&atilde;n l&agrave; &ldquo;&Iacute;ch tr&iacute; quả&rdquo;, v&igrave; đ&acirc;y l&agrave; thứ quả c&oacute; t&aacute;c dụng dưỡng huyết &iacute;ch tr&iacute; thần hiệu. Quả nh&atilde;n c&oacute; h&igrave;nh tr&ograve;n n&ecirc;n c&ograve;n c&oacute; t&ecirc;n gọi &ldquo;Quế vi&ecirc;n&rdquo; ( vi&ecirc;n l&agrave; tr&ograve;n), hoặc c&oacute; t&ecirc;n gọi l&agrave; &ldquo;Lệ chi n&ocirc;&rdquo; do m&ugrave;a nh&atilde;n tiếp sau m&ugrave;a vải ( n&ocirc; l&agrave; kẻ theo hầu).<br />\r\nTheo Đ&ocirc;ng y, Long nh&atilde;n nhục hay Long nh&atilde;n c&oacute; vị cam, t&iacute;nh &ocirc;n; quy v&agrave;o kinh T&acirc;m v&agrave; Tỳ.<br />\r\nC&oacute; t&aacute;c dụng bổ &iacute;ch t&acirc;m tỳ, dưỡng huyết an thần; Chủ trị trống ngực hồi hộp tim loạn nhịp,, mất ngủ hay qu&ecirc;n, k&eacute;m ăn mệt mỏi, đại tiện ra m&aacute;u, phụ nữ băng lậu..<br />\r\n<br />\r\nLong nh&atilde;n c&oacute; h&agrave;m lượng dinh dưỡng cao, qu&aacute; tr&igrave;nh sấy kh&ocirc; khiến long nh&atilde;n bay hơi gần hết lượng nước. Trong Long nh&atilde;n chứa đường glucose, lipid, vitamin B1,B2,C, P v&agrave; c&aacute;c nguy&ecirc;n tố Canxi, Phospho, sắt..<br />\r\n<br />\r\nC&aacute;c nghi&ecirc;n cứu dược l&yacute; hiện đại cho thấy, Long nh&atilde;n c&oacute; t&aacute;c dụng chống l&atilde;o suy v&igrave; trong c&ugrave;i c&oacute; Flavoprotein &ndash; một hoạt chất c&oacute; t&aacute;c dụng tăng cường hoạt t&iacute;nh của c&aacute;c tế b&agrave;o thần kinh n&atilde;o.<br />\r\nTrong c&ugrave;i nh&atilde;n c&ograve;n c&oacute; Vitamin PP, c&oacute; t&aacute;c dụng l&agrave;m tăng độ bền v&agrave; độ đ&agrave;n hồi của mạch m&aacute;u, gi&uacute;p qu&aacute; tr&igrave;nh tuần ho&agrave;n m&aacute;u tốt hơn.</p>\r\n', '2016-11-28 16:47:00'),
(6, 'Mạn đàm về trà', 'tra.jpg', '<p>&quot;Mối t&igrave;nh đầu của t&ocirc;i<br />\r\nL&agrave; cơn mưa giăng giăng ngo&agrave;i cửa lớp,<br />\r\nL&agrave; &aacute;o ai bay trắng cả giấc mơ<br />\r\nL&agrave; b&agrave;i thơ c&ograve;n ho&agrave;i trong vở,<br />\r\nGiữa giờ chơi mang đến lại mang về&quot;<br />\r\n-Phượng Hồng-</p>\r\n\r\n<p>Giống như c&aacute;i c&aacute;ch người ta hay v&iacute; cafe l&agrave; người t&igrave;nh l&acirc;u năm, t&ocirc;i v&iacute; tr&agrave; như mối t&igrave;nh đầu.<br />\r\nBạn t&ocirc;i từng ngạc nhi&ecirc;n hỏi lại rằng &quot;Ơ hay, tr&agrave; l&agrave; phải gi&agrave; phải cổ k&iacute;nh chứ sao lại l&agrave; mối t&igrave;nh đầu?&quot;<br />\r\nT&ocirc;i chỉ cười bởi v&igrave; t&ocirc;i cũng chẳng hiểu v&igrave; sao.<br />\r\nVới t&ocirc;i, tr&agrave; như một thiếu nữ, một b&ocirc;ng hoa chớm nở, chẳng nồng n&agrave;n đ&acirc;u nhưng lại &acirc;m thầm nhẹ nh&agrave;ng n&iacute;u lấy tr&aacute;i tim t&ocirc;i, bằng cả hương lẫn vị<br />\r\nB&igrave;nh dị đấy m&agrave; sao xa lạ qu&aacute;.<br />\r\nNhư mối t&igrave;nh đầu, nhẹ nh&agrave;ng, e ấp, tinh kh&ocirc;i<br />\r\nMối t&igrave;nh m&agrave; t&ocirc;i đ&atilde; phải l&ograve;ng từ c&aacute;i nh&igrave;n đầu ti&ecirc;n!</p>\r\n', '2016-11-28 16:50:16');

