--

INSERT INTO `products` (`pid`, `name`, `price`, `quantity`, `image`, `packing_method`, `description`, `created_at`, `rating`, `rating_times`, `sold`, `oeid`) VALUES
(1, 'Cúc hoa', 78000, 189, 'cuchoa.jpg', 1, '<p>&nbsp;Vừa đẹp, vừa thơm, vừa tốt cho sức khoẻ lại l&agrave;m đẹp da.</p>\r\n\r\n<p>&bull;C&uacute;c hoa t&ecirc;n khoa học Chrysanthemum morifolium Ramat, thuộc học C&uacute;c (Asteraceae).<br />\r\n&bull; T&ecirc;n H&aacute;n Việt kh&aacute;c: Nữ hoa, Nữ tiết, Nhật tinh, Mẫu c&uacute;c&hellip;<br />\r\n<br />\r\n&bull; Theo Y học Cổ truyền Hoa C&uacute;c vị hơi ngọt, đắng, t&iacute;nh m&aacute;t, kh&ocirc;ng độc.<br />\r\nC&oacute; t&aacute;c dụng thanh nhiệt, giải độc,giải cảm, m&aacute;t gan, s&aacute;ng mắt, dịu thần kinh&hellip; V&igrave; vậy thường được d&ugrave;ng để chữa c&aacute;c chứng do phong nhiệt: ch&oacute;ng mặt, nhức đầu, đau đầu, chảy nước mắt, đinh nhọt, sốt, cao huyết &aacute;p, mất ngủ, căng thẳng&hellip; Hoặc d&ugrave;ng ngo&agrave;i để đắp, rửa mụn nhọt.<br />\r\n<br />\r\n&bull; C&aacute;c nghi&ecirc;n cứu khoa học về Dược l&yacute; của C&uacute;c hoa đều cho thấy C&uacute;c hoa c&oacute; thể gi&uacute;p kh&aacute;ng khuẩn, kh&aacute;ng virus g&acirc;y cảm c&uacute;m, ngăn ngừa tế b&agrave;o ung thư lan rộng v&agrave; khiến ch&uacute;ng nhạy cảm hơn với c&aacute;c thuốc điều trị ung thư. C&uacute;c hoa l&agrave;m gi&atilde;n mạch m&aacute;u, hạ huyết &aacute;p, giảm mỡ trong m&aacute;u,gi&uacute;p s&aacute;ng mắt, l&agrave;m dịu căng thẳng thần kinh v&agrave; gi&uacute;p ngủ ngon, ức chế một số loại nấm ngo&agrave;i da.<br />\r\n<br />\r\n&bull; Một số c&aacute;ch d&ugrave;ng C&uacute;c hoa:<br />\r\n+ C&uacute;c, Mật ong: t&aacute;c dụng an thần, s&aacute;ng mắt, đẹp da.<br />\r\n10g C&uacute;c hoa, 30ml Mật ong.<br />\r\nCho hoa C&uacute;c v&agrave;o ly, tr&aacute;ng với nước ấm. Sau đ&oacute; cho tiếp nước s&ocirc;i,đậy nắp trong 3 ph&uacute;t,khi d&ugrave;ng cho th&ecirc;m mật ong v&agrave;o. C&oacute; thể ăn c&ugrave;ng với mứt hạt sen, long nh&atilde;n..<br />\r\n<br />\r\n+ C&uacute;c, Cam thảo: t&aacute;c dụng giảm b&eacute;o, thanh nhiệt, m&aacute;t gan, s&aacute;ng mắt.<br />\r\n10g C&uacute;c hoa, 10g Cam thảo, 2 th&igrave;a đường ph&egrave;n.<br />\r\nĐun s&ocirc;i nước, cho C&uacute;c, Cam thảo, đường ph&egrave; v&agrave;o đun nhỏ lửa trong 15 ph&uacute;t rồi tắt. Lọc bỏ b&atilde; lấy nước d&ugrave;ng. C&oacute; thể th&acirc;m chanh, hoặc đợi nguội để v&agrave;o tủ lạnh d&ugrave;ng dần.</p>\r\n', '2016-11-28 15:59:50', 0, 0, 11, 2),
(2, 'Hồng táo', 108000, 200, 'hongtao.jpg', 1, '<p>Hồng t&aacute;o c&oacute; gi&aacute; trị dinh dưỡng rất cao với nhiều loại protein, lipid, acid amin, c&aacute;c vitamin A, B2, C, P, c&aacute;c nguy&ecirc;n tố Canxi, Photpho, Sắt&hellip;<br />\r\n<br />\r\n&bull; Hồng t&aacute;o t&aacute;c dụng bổ gan, tăng cường cơ bắp, hạ huyết &aacute;p, ăn thần, l&agrave;m tan đờm, giảm ho, cải thiện dinh dưỡng cơ tim, hạn chế sự ph&aacute;t triển của tế b&agrave;o ung thư.<br />\r\n<br />\r\n&bull; Một số c&aacute;ch d&ugrave;ng:<br />\r\n1. Tr&agrave; D&acirc;u t&acirc;y, T&aacute;o:<br />\r\nNguy&ecirc;n liệu: D&acirc;u t&acirc;y, Hồng t&aacute;o, Đường, Đ&aacute;<br />\r\nNg&acirc;m D&acirc;u t&acirc;y với nước muối, để r&aacute;o, bỏ cuống v&agrave; bổ đ&ocirc;i.<br />\r\nT&aacute;o c&aacute;t nhỏ<br />\r\nCho t&aacute;o v&agrave; ấm, cho nước vừa đủ, đun s&ocirc;i khoảng 5 ph&uacute;t rồi th&ecirc;m đường, khẩu vị t&ugrave;y &yacute;.<br />\r\nXếp D&acirc;u t&acirc;y sẵn v&agrave;o cốc, r&oacute;t nước tr&ecirc;n v&agrave;o, đợi khoảng 2-3 ph&uacute;t l&agrave; được, c&oacute; thể cho đ&aacute; nếu th&iacute;ch uống lạnh.<br />\r\n<br />\r\n2. Tr&agrave; T&aacute;o L&ecirc;:<br />\r\nNguy&ecirc;n liệu: 25 quả Hồng t&aacute;o kh&ocirc;, 1 quả l&ecirc;, 1 củ gừng nhỏ, 2-3 thanh quế, đường hoặc mật ong.<br />\r\nL&ecirc; bổ l&agrave;m tư, kh&ocirc;ng cần gọt vỏ, bỏ hạt.<br />\r\nGừng rửa sạch, th&aacute;i l&aacute;t. T&aacute;o th&aacute;i l&aacute;t.<br />\r\nCho tất cả c&aacute;c nguy&ecirc;n liệu v&agrave;o nồi, đun s&ocirc;i, cho nhỏ lửa hầm khoảng 1 tiếng. C&oacute; thể uống ngay hoặc để tủ lạnh uống m&aacute;t, khi uống cho th&ecirc;m đường t&ugrave;y &yacute;.</p>\r\n', '2016-11-28 16:03:57', 0, 0, 0, 3),
(3, 'Sơn tra', 148000, 200, 'sontra.jpg', 2, '<p>&bull; T&ecirc;n khoa học l&agrave; Crataegus cuneara Sied.et Zucc.<br />\r\n<br />\r\n&bull; Theo Y học Cổ truyền Sơn tra vị chua, ngọt, t&iacute;nh ấm, quy kinh tỳ, vị, can.<br />\r\nC&ocirc;ng năng ph&aacute; kh&iacute; t&aacute;n ứ, h&oacute;a đờm, chỉ huyết&hellip;<br />\r\n<br />\r\n&bull; C&ocirc;ng dụng của sơn tra:<br />\r\n- K&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, tan dầu mỡ, gi&uacute;p ăn ngon miệng.<br />\r\n- H&ograve;a tan m&aacute;u cục.<br />\r\n- Giảm đau do t&iacute;ch trệ, ăn kh&ocirc;ng ti&ecirc;u, đau bụng do ứ huyết.<br />\r\n- Tan mỡ thừa, chất b&eacute;o trong người.<br />\r\n<br />\r\n&bull; C&aacute;ch sử dụng:<br />\r\n- Những người muốn giảm mỡ, giảm c&acirc;n, giảm cholesteron m&aacute;u: n&ecirc;n d&ugrave;ng Sơn tra kh&ocirc; nấu nước uống như nước tr&agrave;, n&ecirc;n d&ugrave;ng trước bữa ăn.<br />\r\n- Những người ăn uống kh&oacute; ti&ecirc;u, đầy bụng, chướng hơi: d&ugrave;ng sau bữa ăn.<br />\r\n<br />\r\n&bull; Một số c&aacute;ch d&ugrave;ng Sơn tra:<br />\r\n- Sơn tra sắc uống thay tr&agrave; h&agrave;ng ng&agrave;y. Chữa chứng ăn uống kh&oacute; ti&ecirc;u, bụng đầy tức, đau.&nbsp;<br />\r\n- Sơn tra, l&aacute; sen, lượng bằng nhau, sắc lấy nước uống thay tr&agrave; h&agrave;ng ng&agrave;y. Chữa cao huyết &aacute;p, mỡ m&aacute;u&nbsp;<br />\r\n- Sơn tra, r&acirc;u ng&ocirc; lượng bằng nhau, sắc uống thay tr&agrave; h&agrave;ng ng&agrave;y. Chữa b&eacute;o ph&igrave;.&nbsp;<br />\r\n- Sơn tra ng&acirc;m Mật ong: d&ugrave;ng cho những người bệnh mạch v&agrave;nh v&agrave; chứng ăn kh&ocirc;ng ti&ecirc;u g&acirc;y ti&ecirc;u chảy.<br />\r\nSơn tra 500g, Mật ong 250ml.<br />\r\nCho Sơn tra v&agrave;o nồi, đổ nước vừa đủ,đun ch&iacute;n khoảng 7/10. Khi nước sắp cạn, cho mật ong v&agrave;o đun đến khi nhừ th&igrave; gạn nước ra để nguội, cho v&agrave;o lọ d&ugrave;ng dần. Ng&agrave;y ăn 3 lần, mỗi lần 15-30ml.<br />\r\n- Tr&agrave; giảm mỡ: Th&iacute;ch hợp cho người bị mỡ m&aacute;u cao, cơ thể b&eacute;o, hay tức ngực, ch&oacute;ng mặt, buồn n&ocirc;n, n&ocirc;n mửa&hellip;<br />\r\nSơn tra 10g, L&aacute; sen kh&ocirc; 60g, hạt &Yacute; dĩ sống 20g, L&aacute; lạc 15g, vỏ qu&yacute;t 5g, l&aacute; Tr&agrave; 60g. Tất cả đem nghiền bột, ng&acirc;m h&atilde;m trong nước s&ocirc;i lấy nước uống thay tr&agrave;. Mỗi đợt uống 5-10 ng&agrave;y.&nbsp;</p>\r\n', '2016-11-28 16:06:28', 0, 0, 0, 4),
(4, 'Lá sen', 38000, 200, 'lasen.jpg', 1, '<p>&bull; T&aacute;c dụng của l&aacute; sen:<br />\r\n- Giải co thắt cơ trơn, hạ huyết &aacute;p.<br />\r\n- Giảm đau, an thần, chống rồi loạn nhịp tim, cầm m&aacute;u.<br />\r\n- Chống oxi h&oacute;a Lipid m&agrave;ng tế b&agrave;o, chữa chảy m&aacute;u như đại tiện ra m&aacute;u, chảy m&aacute;u ch&acirc;n răng, xuất huyết dưới da, sốt xuất huyết.<br />\r\n- Điều h&ograve;a Lipid m&aacute;u theo hướng giảm Lipid tự do v&agrave; Cholesteron m&aacute;u, rất c&oacute; lợi cho người b&eacute;o.<br />\r\n<br />\r\n&bull; C&aacute;c c&aacute;ch d&ugrave;ng L&aacute; Sen:<br />\r\n- L&aacute; kh&ocirc; đ&atilde; th&aacute;i cho v&agrave;o ấm đun s&ocirc;i lấy nước uống h&agrave;ng ng&agrave;y.<br />\r\n- H&atilde;m với nước s&ocirc;i trong 2-5 ph&uacute;t. C&oacute; thể th&ecirc;m ch&uacute;t Quế, Hoa hồng hoặc Đường .<br />\r\n- Đun l&aacute; sen lấy nước, th&ecirc;m Gạo tẻ 100g, đem nấu ch&aacute;o, d&ugrave;ng với đường trắng. Hoặc c&oacute; thể th&ecirc;m đậu xanh để tăng sức thanh nhiệt, giải độc.</p>\r\n', '2016-11-28 16:08:20', 5, 1, 0, 3),
(5, 'Kỷ tử', 186000, 199, 'kytu.jpg', 2, '<p>&bull; Tr&agrave; Kỷ tử đ&atilde; được lưu truyền rộng r&atilde;i trong d&acirc;n gian ở nhiều nước phương Đ&ocirc;ng, v&agrave; được ghi ch&eacute;p lại trong c&aacute;c s&aacute;ch về tr&agrave; dược cổ truyền. Tr&agrave; Kỷ tử l&agrave; một trong những loại tr&agrave; thuốc hết sức đơn giản m&agrave; c&ocirc;ng dụng lại rất tốt.<br />\r\nChỉ cần mỗi ng&agrave;y d&ugrave;ng 15g Kỷ tử h&atilde;m với nước n&oacute;ng, chờ khoảng 15-20 ph&uacute;t l&agrave; ta đ&atilde; c&oacute; được thứ nước m&agrave;u đỏ đẹp, thơm ngon, vừa c&oacute; t&aacute;c dụng giải kh&aacute;t trong ng&agrave;y thay tr&agrave;, lại vừa c&oacute; c&ocirc;ng năng bổ thận &iacute;ch tinh, dưỡng can minh mục.<br />\r\n<br />\r\n&bull; Theo Y học cổ truyền Kỷ tử vị ngọt, t&iacute;nh m&aacute;t, kh&ocirc;ng độc. T&aacute;c dụng tư bổ can thận, minh mục, nhuận phế, bổ tinh huyết, sinh t&acirc;n.&nbsp;<br />\r\nNgười xưa thường d&ugrave;ng tr&agrave; Kỷ tử để bồi bổ cơ thể v&agrave; ph&ograve;ng chống t&iacute;ch cực c&aacute;c chứng bệnh suy nhược cơ thể, đau đầu hoa mắt, giảm thị lực, thiếu m&aacute;u, lưng đau, gối mỏi, suy giảm t&igrave;nh dục, di mộng tinh, muộn con..<br />\r\n<br />\r\nTrong Kỷ tử c&oacute; chứa nhiều acid amin, c&aacute;c vitamin B1,B2, vitamin C, caroten, sắt, canxi..<br />\r\n<br />\r\n&bull; T&aacute;c dụng dược l&yacute;:<br />\r\n1. Tăng cường miễn dịch kh&ocirc;ng đặc hiệu<br />\r\n2. Th&uacute;c đẩy qu&aacute; tr&igrave;nh tạo m&aacute;u của tủy xương.<br />\r\n3. Ức chế sự lắng đọng mỡ trong gan, bảo vệ tế b&agrave;o gan, th&uacute;c đẩy qu&aacute; tr&igrave;nh t&aacute;i sinh tế b&agrave;o gan.<br />\r\n4. Gi&atilde;n mạch v&agrave; hạ huyết &aacute;p.<br />\r\n5. Hạ đường huyết.<br />\r\n6. Chống oxi h&oacute;a v&agrave; ngăn chặn sự l&atilde;o h&oacute;a.<br />\r\n7. N&acirc;ng cao sức chịu đựng của cơ thể trong điều kiện thiếu oxi, ph&ograve;ng chống trạng th&aacute;i mệt mỏi.<br />\r\n8. Chống ph&oacute;ng xạ v&agrave; ngăn ngừa tế b&agrave;o ung thư ph&aacute;t triển.<br />\r\n<br />\r\n&bull; Một số c&aacute;ch d&ugrave;ng kh&aacute;c:<br />\r\n1. Trị n&aacute;m mặt, da mặt sần s&ugrave;i: Kỷ tử, Sinh địa tỉ lệ 10:3, t&aacute;n bột, mỗi lần uống 1 th&igrave;a nhỏ với rượu n&oacute;ng, ng&agrave;y uống 3 lần, uống l&acirc;u da đẹp như trẻ con.<br />\r\n<br />\r\n2. Giảm mỡ m&aacute;u, s&aacute;ng mắt, giảm nếp nhăn: Kỷ tử 6g, C&uacute;c hoa 4g, l&aacute; d&acirc;u tằm 3g, hạt muồng ( thảo quyết minh) 3g.<br />\r\nH&atilde;m nước s&ocirc;i, uống thay nước trong ng&agrave;y.<br />\r\n<br />\r\n3. Ngo&agrave;i ra t&ugrave;y v&agrave;o mục đ&iacute;ch c&oacute; thể kết hợp Kỷ tử với một số vị thuốc kh&aacute;c như: Đương quy, T&aacute;o t&agrave;u ( dưỡng huyết, tăng tạo m&aacute;u); T&aacute;o nh&acirc;n, ngũ vị ( dưỡng t&acirc;m, an thần); Thảo quyết minh, H&agrave; thủ &ocirc; ( bổ can thận, hạ mỡ m&aacute;u)&hellip;.<br />\r\n4. Hoặc c&oacute; thể d&ugrave;ng Kỷ tử hầm g&agrave;, hấp c&aacute;&hellip;.</p>\r\n', '2016-11-28 16:10:16', 0, 0, 1, 5),
(6, 'Hoa hoè', 98000, 197, 'hoahoe.jpg', 1, '<p>&bull; Trong hoa H&ograve;e chứa lượng lớn Rutin( 6-30%), c&oacute; t&aacute;c dụng:<br />\r\n+ Tăng cường sức bền của mạch m&aacute;u, giảm t&iacute;nh thấm mao mạch.<br />\r\n+ Với hệ tim mạch: hạ huyết &aacute;p, gi&atilde;n mạch v&agrave;nh.<br />\r\n+ Cầm m&aacute;u: giảm thời gian chảy m&aacute;u, nếu sao th&agrave;nh than t&aacute;c dụng mạnh hơn.<br />\r\n+ Giảm Cholesteron m&aacute;u v&agrave; gan.<br />\r\n+ Chống co thắt v&agrave; chống lo&eacute;t.<br />\r\n+ Chống ti&ecirc;u chảy, k&iacute;ch th&iacute;ch ni&ecirc;m mạc ruột tiết dịch.<br />\r\n+ Kh&aacute;ng vi&ecirc;m.<br />\r\nDo vậy hoa H&ograve;e thường được d&ugrave;ng cho những người cao huyết &aacute;p,đau đầu, hoa mắt, đề ph&ograve;ng đứt vỡ mạch m&aacute;u, điều trị trĩ, chảy m&aacute;u ch&acirc;n răng, chảy m&aacute;u cam, ho ra m&aacute;u, đại tiện ra m&aacute;u, băng huyết, xuất huyết dưới da&hellip;<br />\r\n<br />\r\nMột số c&aacute;ch d&ugrave;ng hoa H&ograve;e:<br />\r\n1. Lấy lượng hoa Hờ vừa đủ, tr&aacute;ng nhanh với nước s&ocirc;i. Sau đ&oacute; th&ecirc;m nước, đậy nắp đợi 3-5 ph&uacute;t cho ngấm l&agrave; d&ugrave;ng được.<br />\r\n<br />\r\n2. Trị cao huyết &aacute;p: hoa H&ograve;e 10g, l&aacute; Sen 10g, ng&oacute; Sen 5g, hoa C&uacute;c 4g.<br />\r\nCho 400ml nước sắc c&ograve;n 100ml, uống trong ng&agrave;y.<br />\r\n3. Chữa đau đầu m&ugrave;a nắng n&oacute;ng: hoa H&ograve;e 10g( sao thơm), hạt Muồng 20g( sao đen), hoa C&uacute;c 5g. H&atilde;m với nước s&ocirc;i, uống thay nước ch&egrave; trong ng&agrave;y.<br />\r\n4. Chữa đại tiện ra m&aacute;u, trĩ chảy m&aacute;u: hoa H&ograve;e, trắc b&aacute;ch diệp mỗi vị 10g, sao ch&aacute;y, kinh giới 10g.<br />\r\nCho 400ml nước sắc c&ograve;n 100ml, uống 2 lần/ ng&agrave;y. Uống liền 3-5ng&agrave;y.</p>\r\n', '2016-11-28 16:11:26', 0, 0, 3, 3),
(7, 'Cam thảo', 156000, 200, 'camthao.jpg', 2, '<p>&bull; C&ocirc;ng dụng:<br />\r\n+Giải độc.<br />\r\n+ Chống co thắt cơ trơn, chống lo&eacute;t, ức chế tiết acid dịch vị, ức chế Histamin.<br />\r\n+ K&iacute;ch th&iacute;ch xuất tiết dịch hầu họng v&agrave; kh&iacute; quản, l&agrave;m lo&atilde;ng đờm<br />\r\n+ Chữa lo&eacute;t dạ d&agrave;y, ruột: Cam thảo sắc uống, hoặc cao Cam thảo. Ng&agrave;y 4g, uống 1 đợt 7-14 ng&agrave;y rồi nghỉ v&agrave;i ng&agrave;y.<br />\r\n+ Chống c&aacute;c h&oacute;a chất g&acirc;y ung thư gan, bảo vệ tế b&agrave;o gan.<br />\r\n+ Kh&aacute;ng khuẩn v&agrave; kh&aacute;ng vi&ecirc;m.<br />\r\n+ Giải nhiệt, chống lợi niệu.<br />\r\n+ L&agrave;m mềm v&agrave; dịu da.</p>\r\n', '2016-11-28 16:13:20', 3, 1, 0, 2),
(8, 'Đẳng sâm', 118000, 194, 'dangsam.jpg', 1, '<p><br />\r\n&bull; Theo Y học cổ truyền Đẳng s&acirc;m Vị ngọt, t&iacute;nh b&igrave;nh, quy kinh tỳ, phế.<br />\r\n&bull; T&aacute;c dụng: bổ trung &iacute;ch kh&iacute;, sinh t&acirc;n, kiện tỳ, dưỡng huyết. D&ugrave;ng trong c&aacute;c trường hợp tỳ vị hư nhược, kh&iacute; hư huyết hư, mệt mỏi, ăn k&eacute;m, đại tiện lỏng, sa tử cung, sa trực tr&agrave;ng&hellip;<br />\r\n<br />\r\n&bull; Một số c&aacute;ch d&ugrave;ng:<br />\r\n+ Huyết hư thiếu m&aacute;u, suy nhược cơ thể: Đẳng s&acirc;m 15g, Đại t&aacute;o 20 quả.<br />\r\nH&atilde;m trong nước s&ocirc;i khoảng 30-45 ph&uacute;t, uống v&agrave; ăn cả s&acirc;m t&aacute;o.<br />\r\n<br />\r\n+ Ch&aacute;o S&acirc;m Kỳ Đại t&aacute;o: Sinh Kỳ 30g, Đẳng s&acirc;m 30g, Cam thảo 12g, Đại t&aacute;o 10 quả, Gạo tẻ 100g.<br />\r\nSắc c&aacute;c dược liệu lấy nước, gạo tẻ v&agrave; đại t&aacute;o nấu với nước tr&ecirc;n th&agrave;nh ch&aacute;o, chia 2 lần ăn trong ng&agrave;y.<br />\r\n⇨ D&ugrave;ng trong c&aacute;c trường hợp người mệt mỏi, ăn k&eacute;m, suy nhược..<br />\r\n<br />\r\n+ Ch&aacute;o S&acirc;m, &Yacute;, Linh: Đẳng s&acirc;m 20g, &Yacute; dĩ 16g, Phục linh 12g, Gạo nếp 100g.<br />\r\nĐẳng s&acirc;m v&agrave; Phục Linh sắc lấy nước bỏ b&atilde;, nấu với gạo, &yacute; dĩ th&agrave;nh ch&aacute;o.<br />\r\n⇨ D&ugrave;ng trong c&aacute;c trường hợp tỳ vị hư nhược, kh&iacute; huyết hư, cơ thể gầy yếu, ăn k&eacute;m, kh&aacute;t nước, ti&ecirc;u chảy l&acirc;u ng&agrave;y..</p>\r\n', '2016-11-28 16:15:01', 0, 0, 6, 1),
(9, 'Tâm sen', 168000, 200, 'tamsen.jpg', 2, '<p>Sen l&agrave; một biểu tượng kh&ocirc;ng thể t&aacute;ch rời trong văn ho&aacute; người Việt. Kh&ocirc;ng chỉ trong văn ho&aacute;, để l&agrave;m đẹp, sen c&ograve;n l&agrave;m thực phẩm, đặc biệt l&agrave; d&ugrave;ng l&agrave;m thuốc chữa bệnh.<br />\r\nHầu hết c&aacute;c bộ phận của c&acirc;y sen đều được sử dụng l&agrave;m thuốc, từ củ sen, ng&oacute; sen, l&aacute; sen, hoa sen, hạt sen, t&acirc;m sen&hellip;.<br />\r\n<br />\r\nT&acirc;m sen l&agrave; mầm của hạt sen, m&agrave;u xanh. Y học cổ truyền gọi l&agrave; Li&ecirc;n t&acirc;m, hay li&ecirc;n t&acirc;m tử, c&oacute; vị đắng t&iacute;nh h&agrave;n.<br />\r\n<br />\r\nTheo kinh nghiệm d&acirc;n gian, khi mất ngủ ngo&agrave;i việc d&ugrave;ng c&aacute;c thuốc an thần của y học hiện đại, người ta uống tr&agrave; t&acirc;m sen.<br />\r\nĐ&acirc;y kh&ocirc;ng phải l&agrave; c&ocirc;ng dụng duy nhất của loại tr&agrave; n&agrave;y, theo y thư cổ, tr&agrave; t&acirc;m sen c&oacute; c&ocirc;ng dụng thanh t&acirc;m,gi&aacute;ng &aacute;p, s&aacute;p tinh, chỉ huyết..<br />\r\n<br />\r\nT&acirc;m sen c&oacute; chứa asparagin v&agrave; c&aacute;c alkaloid như nelumbin, nuciferin, liensinin&hellip; nucuferin c&oacute; t&aacute;c dụng k&eacute;o d&agrave;i giấc ngủ. nelumbin c&oacute; t&aacute;c dụng trấn tĩnh, an thần. C&aacute;c thử nghiệm tr&ecirc;n l&acirc;m s&agrave;ng đều cho thấy t&acirc;m sen c&oacute; t&aacute;c dụng cường tim v&agrave; hạ huyết &aacute;p.<br />\r\n<br />\r\nTheo c&aacute;c kết quả nghi&ecirc;n cứu hiện đại cho thấy, t&acirc;m sen c&oacute; t&aacute;c dụng hạ &aacute;p th&ocirc;ng qua cơ chế l&agrave;m gi&atilde;n cơ trơn th&agrave;nh mạch, ph&ograve;ng chống rồi loạn nhịp tim, ức chế qu&agrave; tr&igrave;nh ngưng tập tiểu cầu, chống oxi ho&aacute;.<br />\r\nN&oacute; cũng cải thiện t&igrave;nh trạng thiếu m&aacute;u cơ tim nhờ khả năng l&agrave;m gi&atilde;n mạch m&aacute;u, giảm nhu cầu ti&ecirc;u thụ oxi của cơ tim, cải thiện lưu lượng tuần ho&agrave;n động mạch v&agrave;nh.<br />\r\n&bull; C&aacute;ch pha tr&agrave; T&acirc;m sen:<br />\r\n1. cho khoảng 3g t&acirc;m sen v&agrave;o ấm, tr&aacute;ng qua nước n&oacute;ng, sau đ&oacute; đổ tiếp nước n&oacute;ng v&agrave;o đợi khoảng 5-7 ph&uacute;t khi t&acirc;m sen lắng xuống đ&aacute;y l&agrave; uống được.<br />\r\n<br />\r\n2. An thần, g&acirc;y ngủ: T&acirc;m sen 5g, l&aacute; V&ocirc;ng 20g, T&aacute;o nh&acirc;n 10g, hoa nh&agrave;i 10g.<br />\r\nT&acirc;m sen sao thơm, t&aacute;o nh&acirc;n sao đen đập dập, l&aacute; v&ocirc;ng sấy kh&ocirc;, t&aacute;n bột. Đem tất cả trộn đề rồi h&atilde;m với 1 l&iacute;t nước, lấy nước đ&oacute; uống l&agrave;m nhiều lần trong ng&agrave;y.<br />\r\n<br />\r\n3. Chữa kh&oacute; ngủ, t&acirc;m phiền, hồi hộp, lo &acirc;u: T&acirc;m sen 8g, hạt muồng 20g sao kh&ocirc;, mạch m&ocirc;n 15g, d&ugrave;ng 3 thứ n&agrave;y h&atilde;m lấy nước uống thay tr&agrave; trong ng&agrave;y.<br />\r\n<br />\r\n4. Ch&aacute;o thanh nhiệt, lương huyết, dưỡng t&acirc;m, an thần: T&acirc;m sen 5g, gạo tẹ 100g. Hai thứ đem ninh nhừ th&agrave;nh ch&aacute;o, th&ecirc;m ch&uacute;t đường ph&egrave;n chia ra ăn v&agrave;i lần trong ng&agrave;y.<br />\r\nTh&iacute;ch hợp cho người gi&agrave; bị suy nhược cơ thể, hoa mắt ch&oacute;ng mặt, t&aacute;o b&oacute;n k&eacute;o d&agrave;i&hellip;</p>\r\n', '2016-11-28 16:16:50', 0, 0, 0, 7),
(10, 'Hoàng kỳ', 158000, 179, 'hoangky.jpg', 1, '<p>&bull; T&aacute;c dụng dược l&yacute;:<br />\r\n1. Tăng cường chức năng miễn dịch của cơ thể<br />\r\n2. Th&uacute;c đẩy qu&aacute; tr&igrave;nh chuyển h&oacute;a.<br />\r\n3. Lợi tiểu.<br />\r\n4. Bảo vệ gan, chống giảm s&uacute;t Glycogen ở gan.<br />\r\n5. Tăng sức co b&oacute;p của cơ tim.<br />\r\n6. Hạ &aacute;p.<br />\r\n7. Kh&aacute;ng khuẩn.<br />\r\n<br />\r\n&bull; C&aacute;ch pha tr&agrave; Ho&agrave;ng kỳ:<br />\r\n1. Ho&agrave;ng kỳ th&aacute;i l&aacute;t mỏng 5-10g, h&atilde;m bằng nước s&ocirc;i nửa giờ, d&ugrave;ng uống thay tr&agrave;.<br />\r\n<br />\r\n2. Ho&agrave;ng K&igrave; 20g, T&aacute;o t&agrave;u 10 quả. Đổ nước nấu c&ocirc; đặc, d&ugrave;ng nước ăn t&aacute;o.<br />\r\n<br />\r\n3. Ho&agrave;ng kỳ 10g, T&aacute;o t&agrave;u 3 quả, gừng tươi 2 l&aacute;t.<br />\r\nH&atilde;m với nước s&ocirc;i trong 10-15 ph&uacute;t l&agrave; d&ugrave;ng được.</p>\r\n', '2016-11-28 16:18:16', 2, 1, 21, 8),
(11, 'Chanh vàng', 108000, 198, 'chanhvang.jpg', 2, '<p><br />\r\nMỗi ng&agrave;y n&ecirc;n uống một ly nước chanh ấm pha với mật ong để năng cao sức đề kh&aacute;ng, tăng cường hệ thống miễn dịch.<br />\r\n<br />\r\nChanh chứa rất nhiều vitamin C, Canxi, Folate, vitamin B5, B3,vtamin B1, B2, sắt, Phospho, kẽm,chất xơ, Protein&hellip;<br />\r\nV&igrave; vậy chanh v&agrave;ng gi&uacute;p cơ thể khoẻ mạnh v&agrave; tinh thần sảng kho&aacute;i hơn.<br />\r\n<br />\r\nKh&ocirc;ng những vậy, chanh v&agrave;ng c&ograve;n gi&uacute;p tăng cường chức năng ti&ecirc;u ho&aacute;, ph&aacute;t huy khả năng thanh lọc cơ thể, b&agrave;i trừ độc.<br />\r\n<br />\r\nBởi trong chanh v&agrave;ng c&oacute; chứa c&aacute;c acid tr&aacute;i c&acirc;y v&agrave; đường tự nhi&ecirc;n n&ecirc;n n&oacute; c&oacute; rất nhiều c&ocirc;ng dụng trong l&agrave;m đẹp.<br />\r\nChanh v&agrave;ng loại bỏ c&aacute;c tế b&agrave;o da chết, tẩy trắng v&ugrave;ng da bị tối hay sạm m&agrave;u. Tr&agrave; chanh l&agrave; một chất chống oxy ho&aacute; tự nhi&ecirc;n gi&uacute;p k&iacute;ch th&iacute;ch sản xuất collagen trong da của bạn.<br />\r\n<br />\r\nC&aacute;ch pha tr&agrave; chanh v&agrave;ng:<br />\r\nNg&acirc;m v&agrave;i l&aacute;t chanh trong nước ấm, đợi một l&uacute;c l&agrave; d&ugrave;ng được<br />\r\nHoặc cho l&aacute;t chanh th&ecirc;m v&agrave;o c&aacute;c loại tr&agrave; thảo dược kh&aacute;c.<br />\r\nLưu &yacute; kh&ocirc;ng cho chanh v&agrave;o khi nước c&ograve;n n&oacute;ng sẽ l&agrave;m tr&agrave; bị đắng.</p>\r\n', '2016-11-28 16:21:14', 4, 1, 2, 8),
(12, 'Hoa nhài', 138000, 198, 'hoanhai.jpg', 1, '<p>Hoa nh&agrave;i _ lo&agrave;i hoa y&ecirc;u kiều như người con g&aacute;i<br />\r\n<br />\r\nD&ugrave; &quot;mỏng manh&quot; như con g&aacute;i nhưng t&aacute;c dụng đối với sức khoẻ lại kh&ocirc;ng hề &quot;mỏng manh&quot;<br />\r\n<br />\r\n&bull; T&aacute;c dụng :<br />\r\n+ Loại bỏ căng thẳng v&agrave; trầm cảm.<br />\r\n+ Gi&uacute;p điều trị mất ngủ, cao huyết &aacute;p, hoa mắt nhức đầu.<br />\r\n+ Cải thiện ti&ecirc;u h&oacute;a, ngăn ngừa c&aacute;c bệnh như ti&ecirc;u chảy, vi&ecirc;m lo&eacute;t dạ d&agrave;y.<br />\r\n+ Gi&uacute;p loại bỏ c&aacute;c chất độc.<br />\r\n+ Giảm sự t&iacute;ch tụ Cholesteron xấu, giảm nguy cơ bệnh tim v&agrave; đột quy.<br />\r\n+ Gi&uacute;p điều tiết chuyển h&oacute;a Glucose, hữu &iacute;ch trong ngăn ngừa v&agrave; điều trị tiểu đường.<br />\r\n+ Gi&uacute;p đẩy mạnh qu&aacute; tr&igrave;nh trao đổi chất.<br />\r\n+ Cải thiện lưu th&ocirc;ng m&aacute;u.<br />\r\n+ Trị ho v&agrave; giảm đau, giảm sưng v&agrave; vi&ecirc;m khớp.<br />\r\n+ Gi&uacute;p da căng s&aacute;ng mịn, ngăn ngừa l&atilde;o h&oacute;a<br />\r\n<br />\r\n&bull; Một số c&ocirc;ng thức với hoa Nh&agrave;i:<br />\r\n1. Trị mất ngủ: Hoa Nh&agrave;i 6g, T&acirc;m sen 8g. H&atilde;m với nước s&ocirc;i uống h&agrave;ng ng&agrave;y.<br />\r\n<br />\r\n2. Gi&uacute;p thanh nhiệt, tỉnh t&aacute;o: hoa Nh&agrave;i 6g. H&atilde;m với 300ml nước ấm khoảng 5 ph&uacute;t khi tỏa m&ugrave;i thơm l&agrave; d&ugrave;ng được. C&oacute; thể th&ecirc;m mật ong, đường Ph&egrave;n t&ugrave;y khẩu vị.<br />\r\n<br />\r\n3. Trị tăng huyết &aacute;p: Hoa Nh&agrave;i 10g, hoa H&ograve;e 10g, hoa Đại 6g, hoa C&uacute;c 6g. Sắc uống.<br />\r\n<br />\r\n4. Chữa đau đầu ch&oacute;ng mặt: hoa Nh&agrave;i, hoa C&uacute;c đều 6g. H&atilde;m với nước ấm uống thay nước ch&egrave; h&agrave;ng ng&agrave;y.<br />\r\n<br />\r\n5. Chữa mụn nhọt: hoa Nh&agrave;i 10g, Kim ng&acirc;n hoa 20g, Bồ c&ocirc;ng anh 20g, Cam thảo 16g. Sắc uống.</p>\r\n', '2016-11-28 16:22:51', 0, 0, 2, 7),
(13, 'Trà bát bảo', 120000, 200, 'trabatbao.jpg', 3, '<p>Giải độc gan,gi&uacute;p dễ ngủ, điều ho&agrave; huyết &aacute;p, l&agrave;m đẹp da..<br />\r\n<br />\r\nTr&agrave; b&aacute;t bảo c&oacute; thể uống lạnh v&agrave;o m&ugrave;a n&oacute;ng, uống ấm khi trời lạnh, đều rất tốt.&nbsp;<br />\r\n<br />\r\nShop c&oacute; b&aacute;n đầy đủ nguy&ecirc;n liệu để pha tr&agrave; b&aacute;t bảo.<br />\r\nĐ&oacute;ng t&uacute;i đ&atilde; mix đủ c&aacute;c vị.<br />\r\n120k/ 10 g&oacute;i.&nbsp;<br />\r\n<br />\r\nHoặc c&oacute; thể mua ri&ecirc;ng từng vị.&nbsp;<br />\r\n<br />\r\nTrong h&igrave;nh l&agrave; tr&agrave; b&aacute;t bảo gồm c&aacute;c nguy&ecirc;n liệu :<br />\r\nT&acirc;m sen<br />\r\nHồng t&aacute;o<br />\r\nLong nh&atilde;n<br />\r\nKỷ tử<br />\r\nCam thảo<br />\r\nC&uacute;c hoa<br />\r\nHoa nh&agrave;i<br />\r\nTrần b&igrave;<br />\r\nSơn tra.&nbsp;<br />\r\n<br />\r\nC&oacute; thể mix c&aacute;c dược liệu kh&aacute;c như Quả la h&aacute;n, hạt sen, s&acirc;m...<br />\r\n&nbsp;</p>\r\n', '2016-11-28 16:26:22', 0, 0, 0, 1),
(14, 'Nụ tam thất', 168000, 200, 'nutamthat.jpg', 1, '<p>Tam thất l&agrave; một c&acirc;y thuốc qu&yacute;, c&aacute;c bộ phận của c&acirc;y đều được d&ugrave;ng l&agrave;m thuốc, ngo&agrave;i củ tam thất được d&ugrave;ng phổ biến th&igrave; nụ Tam thất cũng được người xưa lưu t&acirc;m nghi&ecirc;n cứu v&agrave; sử dụng.<br />\r\n<br />\r\nTheo dược thư cổ, nụ tam thất c&oacute; vị ngọt, t&iacute;nh m&aacute;t, c&oacute; c&ocirc;ng dụng thanh nhiệt, b&igrave;nh can, gi&aacute;ng &aacute;p, an thần, trấn tĩnh, thường được d&ugrave;ng để chữa c&aacute;c chứng bệnh như cao huyết &aacute;p, hoa mắt ch&oacute;ng mặt, tai &ugrave;, tai điếc, vi&ecirc;m hầu họng m&atilde;n t&iacute;nh..<br />\r\n<br />\r\nTheo y học hiện đại, nụ tam thất c&oacute; c&aacute;c t&aacute;c dụng sau:<br />\r\n- Tăng cường m&aacute;u l&ecirc;n n&atilde;o,chữa c&aacute;c bệnh hoa mắt ch&oacute;ng mặt, cho&aacute;ng, đau đầu do thiếu m&aacute;u l&ecirc;n n&atilde;o.<br />\r\n- Giảm cholesteron to&agrave;n phần, ngăn ngừa xơ vữa mạch m&aacute;u, gi&uacute;p tăng cường tuần ho&agrave;n m&aacute;u.<br />\r\n- Thanh nhi&ecirc;t<br />\r\n- Bổ huyết, cầm m&aacute;u, ti&ecirc;u ứ huyết.<br />\r\n- Chống vi&ecirc;m tấy, giảm đau<br />\r\n- Hạ huyết &aacute;p, ph&ograve;ng ngừa c&aacute;c biến chứng tim mạch, tai biến mạch m&aacute;u n&atilde;o, ngăn ngừa chứng l&uacute; lẫn ở người gi&agrave;.<br />\r\n- Giảm căng thẳng mệt mỏi, gi&uacute;p tăng lực mạnh, tăng khả năng l&agrave;m việc.<br />\r\n- Ngăn ngừa v&agrave; ph&ograve;ng chống bệnh, k&iacute;ch th&iacute;ch hệ miễn dịch, ức chế vi khuẩn v&agrave; si&ecirc;u vi khuẩn.<br />\r\nTăng sức đề kh&aacute;ng gi&uacute;p cơ thể ph&ograve;ng chống bệnh tật về l&acirc;u d&agrave;i, sử dụng thường xuy&ecirc;n sẽ n&acirc;ng cao tuổi thọ.<br />\r\n- Ngăn ngừa, ph&ograve;ng chống ung thư, ức chế sự h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển của khối u.<br />\r\n- Hạ đường huyết, gi&uacute;p bệnh nh&acirc;n tiểu đường khỏe mạnh v&agrave; giảm c&aacute;c biến chứng do tiểu đường g&acirc;y ra.<br />\r\n- Tăng cường chức năng giải độc gan, hạ men gan, điều chỉnh rối loạn mỡ, rất tốt cho người bị gan nhiễm mỡ.<br />\r\n- L&agrave;m đẹp, giảm b&eacute;o, chống l&atilde;o h&oacute;a, hỗ trợ điều trị n&aacute;m da, gi&uacute;p l&agrave;n da s&aacute;ng đẹp.<br />\r\n- Lợi sữa cho phụ nữ sau sinh.<br />\r\n<br />\r\n&bull; C&aacute;ch d&ugrave;ng:<br />\r\nMỗi ng&agrave;y d&ugrave;ng 2-3g nụ tam thất, tr&aacute;ng nhanh bằng nước n&oacute;ng, rồi th&ecirc;m nước n&oacute;ng đậy nắp lại, chờ trong v&agrave;i ph&uacute;t l&agrave; d&ugrave;ng được.<br />\r\nC&oacute; thể d&ugrave;ng thay nước lọc h&agrave;ng ng&agrave;y được.</p>\r\n', '2016-11-28 16:29:52', 0, 0, 0, 3),
(15, 'Hoa hồng', 148000, 190, 'hoahong.jpg', 1, '<p>Hoa hồng chứa h&agrave;m lượng vitamin cao, phong ph&uacute;, gồm vitamin C,B,K, caroten..<br />\r\n<br />\r\nHoa hồng c&oacute; khả năng c&acirc;n bằng nội tiết, tạo cảm gi&aacute;c dễ chịu, giảm cholesteron trong m&aacute;u,chống oxi h&oacute;a, gi&uacute;p giảm stress, dễ ngủ, cải thiện l&agrave;n da. C&oacute; thể d&ugrave;ng để rửa hoặc đắp mặt, x&ocirc;ng mặt l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, gi&uacute;p da căng mịn, mờ c&aacute;c sắc tố đen.<br />\r\n<br />\r\nMột số c&aacute;ch d&ugrave;ng Hoa hồng:<br />\r\n<br />\r\n- Tr&agrave; Hoa hồng, Kỷ tử, &Iacute;ch mẫu: giảm mụn, bổ m&aacute;u, giảm đau, gi&uacute;p da tươi tắn, hồng h&agrave;o.<br />\r\n<br />\r\n- Tr&agrave; Hoa hồng, &Ocirc; mai: giảm mỡ, da s&aacute;ng mịn.<br />\r\nHoa hồng 10 b&ocirc;ng, &Ocirc; mai 3 quả. H&atilde;m với nước n&oacute;ng trong 8 ph&uacute;t l&agrave; d&ugrave;ng được.<br />\r\n<br />\r\n- Tr&agrave; Hoa hồng, Kỷ tử: hoạt huyết giảm đau, dưỡng gan s&aacute;ng mắt, thanh nhiệt giải kh&aacute;t, th&uacute;c đẩy qu&aacute; tr&igrave;nh tạo m&aacute;u, dưỡng nhan sắc, ức chế ung thư.<br />\r\nHoa hồng 20 b&ocirc;ng, Kỷ tử 20 quả. H&atilde;m với nước s&ocirc;i khoảng 5 ph&uacute;t.<br />\r\n<br />\r\n- Tr&agrave; Hoa hồng, Hồng t&aacute;o: Giảm đau, dưỡng t&acirc;m an thần, da căng mịn, hồng h&agrave;o.<br />\r\nHoa hồng kh&ocirc; 5 b&ocirc;ng, t&aacute;o đỏ 2 quả.<br />\r\nH&atilde;m với nước n&oacute;ng khoảng 80 độ( kh&ocirc;ng d&ugrave;ng nước n&oacute;ng hơn l&agrave;m mất vitamin C), uống mỗi ng&agrave;y 2 cốc.<br />\r\n<br />\r\n- Ch&aacute;o Hoa hồng: kiện tỳ &iacute;ch kh&iacute;, giảm đau bụng kinh.<br />\r\nHoa hồng kh&ocirc; 15g, Hoa Nh&agrave;i kh&ocirc; 10g, Gạo tẻ 100g, Đường đỏ lượng vừa đủ.<br />\r\nHoa Hồng v&agrave; hoa Nh&agrave;i bỏ hết c&agrave;nh, l&aacute;, xay th&agrave;nh bột. Gạo vo sạch, nấu nhừ th&agrave;nh ch&aacute;o, sau đ&oacute; cho hỗn hợp bột hoa Hồng v&agrave; Nh&agrave;i v&agrave;o, th&ecirc;m đường, khuấy tan l&agrave; d&ugrave;ng được.<br />\r\n<br />\r\n- Ch&aacute;o Nếp cẩm, hoa Hồng, Hồng t&aacute;o: kiện tỳ, k&iacute;ch th&iacute;ch tạo m&aacute;u, cải thiện da.<br />\r\nHoa hồng kh&ocirc; 10g, hồng t&aacute;o 10 quả, gạo nếp cẩm, gạo tẻ, đường.<br />\r\nLấy gạo nếp cẩm v&agrave; gạo tẻ tỉ lệ bằng nhau vo sạch,ng&acirc;m nước 1 đ&ecirc;m.<br />\r\nHồng t&aacute;o bỏ hạt, cắt hạt lựu.<br />\r\nHoa hồng ngắt lấy c&aacute;nh, rửa sạch.<br />\r\nCho gạo v&agrave;o nồi,đổ nước s&ocirc;i v&agrave;o đun, cho t&aacute;o đỏ v&agrave;o nấu c&ugrave;ng đến khi ch&aacute;o nhừ th&igrave; cho đường v&agrave;o, đợi đường t&aacute;n hết cho Hoa hồng v&agrave;o, khuấy đều, tắt bếp.<br />\r\nNgo&agrave;i ra, hoa Hồng c&ograve;n d&ugrave;ng để tắm, nấu nước rửa mặt, hoặc x&ocirc;ng hơi. Gi&uacute;p giảm căng thẳng, thư gi&atilde;n, tăng độ ẩm cho da, ti&ecirc;u vi&ecirc;m, se lỗ ch&acirc;n l&ocirc;ng, gi&uacute;p da hồng h&agrave;o, căng mịn, tươi s&aacute;ng.</p>\r\n', '2016-11-28 16:33:19', 4, 2, 13, 1),
(16, 'Hoa ngọc lan', 88000, 194, 'hoangoclan.jpg', 1, '<p>Theo đ&ocirc;ng y, hoa ngọc lan c&oacute; vị đắng, cay, t&iacute;nh ấm; c&oacute; t&aacute;c dụng d&ugrave;ng để chữa ho, vi&ecirc;m mũi, xoang, tiểu kh&oacute;, đầy hơi, buồn n&ocirc;n, sốt, tăng huyết &aacute;p&hellip; Hoa d&ugrave;ng để l&agrave;m thuốc l&agrave; hoa mới chớm nở, c&oacute; thể d&ugrave;ng ở dạng tươi hoặc kh&ocirc; đều được. D&acirc;n gian thường d&ugrave;ng hoa ngọc lan tẩm mật ong trong 3 ng&agrave;y rồi sắc uống như tr&agrave; để chữa ho, đau đầu, hoa mắt rất tốt.<br />\r\nMột số c&aacute;ch d&ugrave;ng hoa Ngọc Lan:&nbsp;<br />\r\n1. Chữa đau bụng kinh: Hoa ngọc lan 12g, sắc hoặc h&atilde;m uống thay tr&agrave; v&agrave;o l&uacute;c s&aacute;ng sớm. D&ugrave;ng 30 ng&agrave;y l&agrave; một liệu tr&igrave;nh, c&oacute; t&aacute;c dụng giảm đau bụng kinh ở phụ nữ.</p>\r\n\r\n<p>2. Chữa v&ocirc; sinh: 10g hoa ngọc lan chưa nở sắc hoặc h&atilde;m uống thay tr&agrave;, v&agrave;o buổi sớm. Cứ 30 ng&agrave;y một liệu tr&igrave;nh, c&oacute; thể cải thiện thống kinh v&agrave; v&ocirc; sinh nữ.</p>\r\n\r\n<p>3. Chữa ho do lạnh: Hoa ngọc lan 20g, đem tẩmmật ong trong 3 ng&agrave;y rồi sắc uống như tr&agrave;.</p>\r\n', '2016-12-01 16:54:57', 0, 0, 6, 1),
(17, 'Kim ngân', 78000, 200, 'kimngan.jpg', 3, '<p>T&ecirc;n khoa học: Lonicera japonica Thunb, họ Cơm ch&aacute;y(Caprifolianceae).</p>\r\n\r\n<p>Kim ng&acirc;n c&oacute; Vị đắng, t&iacute;nh h&agrave;n, kh&ocirc;ng độc. C&oacute; t&aacute;c dụng thanh nhiệt, giải độc, v&igrave; vậy được sử dụng để chữa mụn nhọt, r&ocirc;m sảy, thủy đậu, sởi, tả, lỵ, dị ứng&hellip;</p>\r\n\r\n<p>C&aacute;i T&ecirc;n Kim ng&acirc;n do đặc điểm khi ra hoa của c&acirc;y, những hoa ra sớm sẽ c&oacute; m&agrave;u trắng như bạc, sau 1 thời gian nở c&aacute;c hoa n&agrave;y sẽ chuyển s&agrave;ng m&agrave;u v&agrave;ng. Cho n&ecirc;n tr&ecirc;n c&ugrave;ng một c&acirc;y ta c&oacute; thể thấy cả 2 m&agrave;u sắc hoa c&ugrave;ng hiện diện(kim l&agrave; v&agrave;ng, ng&acirc;n l&agrave; bạc).</p>\r\n\r\n<p>T&aacute;c dụng dược l&yacute;:</p>\r\n\r\n<p>1. T&aacute;c dụng kh&aacute;ng khuẩn, kh&aacute;ng virus: ức chế mạnh đối với tụ cầu, thương h&agrave;n, lỵ Shiga&hellip;<br />\r\n2. T&aacute;c dụng tăng cường chuyển h&oacute;a Lipid.<br />\r\n3. T&aacute;c dụng kh&aacute;ng vi&ecirc;m<br />\r\n4. Hưng phấn trung khu thần kinh: =1/6 caf&eacute;.<br />\r\n5. Lợi tiểu.</p>\r\n\r\n<p>C&aacute;ch d&ugrave;ng:<br />\r\nCho một nh&uacute;m Kim ng&acirc;n v&agrave;o ấm, tr&aacute;ng nhanh qua bằng nước s&ocirc;i. Sau đ&oacute; cho tiếp khoảng 200ml nước s&ocirc;i v&agrave;o, đậy nắp khoảng 5 ph&uacute;t l&agrave; được. Khi d&ugrave;ng c&oacute; thể th&ecirc;m chanh, đường, mật ong.</p>\r\n', '2016-12-01 16:58:20', 0, 0, 0, 2),
(18, 'Long nhãn', 158000, 200, 'longnhan.jpg', 1, '<p>Long nh&atilde;n l&agrave; thực phẩm đ&atilde; qu&aacute; quen thuộc đối với mọi người, c&oacute; thể d&ugrave;ng pha c&ugrave;ng c&agrave;c loại tr&agrave;, hoặc ăn k&egrave;m, hoặc nấu ch&egrave;...</p>\r\n\r\n<p>T&ecirc;n Long nh&atilde;n xuất ph&aacute;t bởi h&igrave;nh dạng giống mắt của con rồng.<br />\r\nS&aacute;ch &ldquo;Thần n&ocirc;ng bản thảo kinh&rdquo; gọi tr&aacute;i nh&atilde;n l&agrave; &ldquo;&Iacute;ch tr&iacute; quả&rdquo;, v&igrave; đ&acirc;y l&agrave; thứ quả c&oacute; t&aacute;c dụng dưỡng huyết &iacute;ch tr&iacute; thần hiệu. Quả nh&atilde;n c&oacute; h&igrave;nh tr&ograve;n n&ecirc;n c&ograve;n c&oacute; t&ecirc;n gọi &ldquo;Quế vi&ecirc;n&rdquo; ( vi&ecirc;n l&agrave; tr&ograve;n), hoặc c&oacute; t&ecirc;n gọi l&agrave; &ldquo;Lệ chi n&ocirc;&rdquo; do m&ugrave;a nh&atilde;n tiếp sau m&ugrave;a vải ( n&ocirc; l&agrave; kẻ theo hầu).<br />\r\nTheo Đ&ocirc;ng y, Long nh&atilde;n nhục hay Long nh&atilde;n c&oacute; vị cam, t&iacute;nh &ocirc;n; quy v&agrave;o kinh T&acirc;m v&agrave; Tỳ.<br />\r\nC&oacute; t&aacute;c dụng bổ &iacute;ch t&acirc;m tỳ, dưỡng huyết an thần; Chủ trị trống ngực hồi hộp tim loạn nhịp,, mất ngủ hay qu&ecirc;n, k&eacute;m ăn mệt mỏi, đại tiện ra m&aacute;u, phụ nữ băng lậu..</p>\r\n\r\n<p>Long nh&atilde;n c&oacute; h&agrave;m lượng dinh dưỡng cao, qu&aacute; tr&igrave;nh sấy kh&ocirc; khiến long nh&atilde;n bay hơi gần hết lượng nước. Trong Long nh&atilde;n chứa đường glucose, lipid, vitamin B1,B2,C, P v&agrave; c&aacute;c nguy&ecirc;n tố Canxi, Phospho, sắt..</p>\r\n\r\n<p>C&aacute;c nghi&ecirc;n cứu dược l&yacute; hiện đại cho thấy, Long nh&atilde;n c&oacute; t&aacute;c dụng chống l&atilde;o suy v&igrave; trong c&ugrave;i c&oacute; Flavoprotein &ndash; một hoạt chất c&oacute; t&aacute;c dụng tăng cường hoạt t&iacute;nh của c&aacute;c tế b&agrave;o thần kinh n&atilde;o.<br />\r\nTrong c&ugrave;i nh&atilde;n c&ograve;n c&oacute; Vitamin PP, c&oacute; t&aacute;c dụng l&agrave;m tăng độ bền v&agrave; độ đ&agrave;n hồi của mạch m&aacute;u, gi&uacute;p qu&aacute; tr&igrave;nh tuần ho&agrave;n m&aacute;u tốt hơn.</p>\r\n', '2016-12-01 17:06:56', 0, 0, 0, 4),
(19, 'Bí đao', 128000, 200, 'bidao.jpg', 1, '<p>Bạn thừa c&acirc;n, bạn lo lắng về đ&aacute;m mỡ ở bụng, ở đ&ugrave;i..., bạn đ&atilde; cố nhịn ăn, tập thể dục đến mệt nhừ người...m&agrave; vẫn kh&ocirc;ng ưng &yacute;?</p>\r\n\r\n<p>B&iacute; đao chư&aacute; nhiều chất xơ, kh&ocirc;ng chứa chất b&eacute;o. Hơn nữa trong b&iacute; đao c&ograve;n chứa hợp chất Hyterin-caperin ngăn kh&ocirc;ng cho đường chuyển ho&aacute; th&agrave;nh mỡ trong cơ thể, n&ecirc;n cơ thể sẽ kh&ocirc;ng bị t&iacute;ch luỹ mỡ thừa.</p>\r\n\r\n<p>Ngo&agrave;i ra trong b&iacute; đao chứa nhiều c&aacute;c vitamin C, vitamin E, v&agrave; c&aacute;c kho&aacute;ng chất Kali, Phospho, Magie&hellip; gi&uacute;p l&agrave;m đẹp cơ thể. B&iacute; đao vốn l&agrave; một phương thuốc l&agrave;m đẹp của c&aacute;c mỹ nh&acirc;n được lưu truyền từ xưa, n&oacute; giữ ẩm cho da, l&agrave;m da căng mịn, s&aacute;ng hồng, bớt dầu v&agrave; mụn đầu đen.</p>\r\n\r\n<p>Chỉ cần cho b&iacute; đao v&agrave;o nước ấm để h&atilde;m lấy nước uống thay nước lọc trong ng&agrave;y. Trong 1 th&aacute;ng bạn sẽ thấy v&oacute;c d&aacute;ng cơ thể được cải thiện r&otilde; rệt&nbsp;</p>\r\n', '2016-12-01 17:08:31', 0, 0, 0, 1),
(20, 'Trà Atiso', 60000, 200, 'tra atiso.jpg', 1, '<h3>T&aacute;c dụng của tr&agrave; atiso</h3>\r\n\r\n<p>&nbsp;Tr&agrave; aitso kh&ocirc;ng những t&aacute;c dụng giảm c&acirc;n c&ograve;n tốt cho t&oacute;c, đẹp da, giải độc m&aacute;t gan, giữ g&igrave;n tuổi thanh xu&acirc;n cho phụ nữ v&agrave; đặc biệt đem lại v&ograve;ng eo thon gọn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bạn c&oacute; thể t&igrave;m đến c&aacute;c loại tr&agrave; atiso đ&atilde; đ&oacute;ng g&oacute;i tr&ecirc;n thị trường, t&igrave;m v&agrave; chọn mua cho m&igrave;nh tr&agrave; atiso th&iacute;ch hợp để giảm c&acirc;n cũng như tốt cho sức khỏe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>C&aacute;ch uống tr&agrave; atiso giảm c&acirc;n hiệu quả</h3>\r\n\r\n<p>Để giảm c&acirc;n hiệu quả c&ugrave;ng tr&agrave; atiso, bạn phải tu&acirc;n thủ một v&agrave;i nguy&ecirc;n tắc sau đ&acirc;y :</p>\r\n\r\n<p>Uống đ&ecirc;u đặn mỗi ng&agrave;y mới ph&aacute;t huy được c&ocirc;ng dụng hiệu quả của atiso</p>\r\n\r\n<p>N&ecirc;n bảo quản tr&agrave; trong thời gian ngắn, tốt nhất l&agrave; pha v&agrave; uống trong ng&agrave;y, để tr&aacute;nh tr&agrave; bị thi&ecirc;u</p>\r\n\r\n<p>Nếu bạn kh&oacute; ngủ, kh&ocirc;ng n&ecirc;n uống tr&agrave; sau 16h, kh&ocirc;ng uống qu&aacute; 1 ly tr&agrave;/ ng&agrave;y</p>\r\n\r\n<p>C&oacute; thể uống tr&agrave; v&agrave;o buổi s&aacute;ng thay cho c&agrave; ph&ecirc; để gi&uacute;p bạn tiết kiệm calo. Uống c&agrave; ph&ecirc; nhiều đường sẽ l&agrave;m bạn tăng c&acirc;n,</p>\r\n\r\n<p>Nếu bạn kh&ocirc;ng tăng c&acirc;n vẫn c&oacute; thể sử dụng tr&agrave; atiso để tăng cường sức khỏe, đẹp da.</p>\r\n', '2016-12-01 17:11:42', 0, 0, 0, 1),
(21, 'Cỏ ngọt sấy khô', 90000, 200, 'co-ngot-say-kho.jpg', 2, '<h3>Đặc điểm</h3>\r\n\r\n<p>L&agrave; một loại cỏ sống l&acirc;u năm, 6 th&aacute;ng sau khi trồng; gốc bắt đầu ho&aacute; gỗ, mỗi gốc c&oacute; nhiều c&agrave;nh (nếu để mọc tự nhi&ecirc;n c&acirc;y c&oacute; thể cao đến 100cm). C&agrave;nh non v&agrave; l&aacute; đều phủ l&ocirc;ng trắng mịn, l&aacute; mọc đối, h&igrave;nh mũi m&aacute;c, d&agrave;i 30-60mm, rộng 15-30mm, c&oacute; 3 g&acirc;n ch&iacute;nh xuất ph&aacute;t từ cuống l&aacute;. M&eacute;p l&aacute; c&oacute; răng cưa ở nửa phần tr&ecirc;n. Cụm hoa h&igrave;nh đầu, mỗi tổng bao c&oacute; chứa 5 hoa nhỏ, tr&agrave;ng h&igrave;nh ống, m&agrave;u trắng ng&agrave;, c&oacute; 5 c&aacute;nh nhỏ. Hoa d&agrave;i 10-12mm. C&oacute; hai v&ograve;i nhuỵ d&agrave;i th&ograve; ra ngo&agrave;i. Hoa c&oacute; m&ugrave;i thơm nhẹ (h&igrave;nh d&aacute;ng giống hoa cỏ L&agrave;o, nhưng nhỏ hơn nhiều). M&ugrave;a hoa từ th&aacute;ng 10 năm trước đến th&aacute;ng 2 năm sau (theo dương lịch).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>To&agrave;n th&acirc;n c&oacute; vị ngọt, nhiều nhất ở l&aacute;, l&aacute; gi&agrave; chết kh&ocirc; ở dưới nhưng cuống rất dai n&ecirc;n kh&ocirc;ng rụng (vẫn c&ograve;n vị ngọt).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cỏ ngọt sinh sản hữu t&iacute;nh (gieo hạt) v&agrave; v&ocirc; t&iacute;nh (gi&acirc;m c&agrave;nh) l&agrave; c&acirc;y ưa ẩm, ưa s&aacute;ng nhưng sợ &uacute;ng v&agrave; chết khi ngập nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ phận d&ugrave;ng: c&agrave;nh mang l&aacute; phơi hoặc sấy kh&ocirc; (Khi đoạn c&agrave;nh d&agrave;i khoảng 20-25cm l&agrave; thời điểm cắt c&agrave;nh. Trung b&igrave;nh mỗi th&aacute;ng một lần thu hoạch)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hoạt chất ch&iacute;nh: Steviosid (l&agrave; một glucosid) c&oacute; vị ngọt gấp 250-300 lần đường k&iacute;nh (saccharoza), nhưng stevioside kh&ocirc;ng sinh năng lượng. Trong Cỏ ngọt kh&ocirc; (cả c&agrave;nh l&aacute;) chứa khoảng 1,5% chất ngọt steviosid (trong l&aacute; chứa khoảng 6-7% steviosid). Như vậy 100g Cỏ ngọt kh&ocirc; c&oacute; lượng chất ngọt tương đương 400-450g đường k&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n thị trường c&oacute; đường Cỏ ngọt dập vi&ecirc;n, mỗi vi&ecirc;n c&oacute; 60mg steviosid tương đương 18g đường k&iacute;nh. Đường cỏ ngọt d&ugrave;ng để pha nước uống như nước chanh, c&agrave; ph&ecirc;...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&aacute;c dụng chữa bệnh: Cỏ ngọt v&agrave; đường Cỏ ngọt kh&ocirc;ng c&oacute; t&aacute;c dụng chữa bệnh m&agrave; l&agrave; chất tạo vị ngọt (kh&ocirc;ng năng lượng) d&ugrave;ng cho:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Người bệnh phải ki&ecirc;ng đường k&iacute;nh (saccharoza) l&agrave; chất sinh năng lượng trong c&aacute;c bệnh như &nbsp;tiểu đường, cắt dạ d&agrave;y, b&eacute;o ph&igrave; cần giảm c&acirc;n&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&aacute;c trường hợp phải ki&ecirc;ng d&ugrave;ng Cam thảo Bắc: người mang thai, người c&oacute; bệnh tim mạch, cao huyết &aacute;p. Người đang d&ugrave;ng thuốc c&oacute; Digitalis, thuốc lợi tiểu nh&oacute;m thiazid (v&iacute; dụ uống tr&agrave; nh&acirc;n trần phải bỏ Cam thảo Bắc, thay bằng Cỏ ngọt...)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>C&aacute;ch d&ugrave;ng</h3>\r\n\r\n<p>Nếu ngửi thấy c&oacute; m&ugrave;i ng&aacute;i, cần khử m&ugrave;i ng&aacute;i; phơi sấy kh&ocirc;, cắt nhỏ Cỏ ngọt để phối hợp với loại thuốc cần tạo vị ngọt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&iacute; dụ: Hoa ho&egrave;, Nh&acirc;n trần, Actis&ocirc;... khi pha tr&agrave; hoặc sắc thuốc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Liều lượng: Tuỳ khẩu vị từng người m&agrave; điều chỉnh lượng Cỏ ngọt cho vừa miệng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;ch khử m&ugrave;i ng&aacute;i: Cỏ ngọt mới l&agrave;m kh&ocirc; sau thu hoạch thường c&oacute; m&ugrave;i ng&aacute;i, g&acirc;y kh&oacute; chịu cho một số người. &nbsp;C&aacute;ch l&agrave;m như sau: Phun nước v&agrave;o Cỏ ngọt kh&ocirc; để l&agrave;m ẩm đều. Cho v&agrave;o t&uacute;i k&iacute;n, ủ trong 2-3 ng&agrave;y rồi đem phơi hoặc sấy kh&ocirc; sẽ hết m&ugrave;i ng&aacute;i m&agrave; kh&ocirc;ng giảm độ ngọt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bảo quản: Phơi hoặc sấy kh&ocirc; rồi bảo quản trong đồ đựng sạch, kh&ocirc;, k&iacute;n. Ch&uacute; &yacute; ph&ograve;ng ẩm, mốc.</p>\r\n', '2016-12-01 17:13:26', 0, 0, 0, 1),
(22, 'Trà Tâm Lan', 50000, 200, 'tra-tam-lan.jpg', 3, '<p><strong>Chức năng:</strong><br />\r\n<br />\r\n- T&aacute;c dụng thanh nhiệt, giải độc, giải kh&aacute;t<br />\r\n<br />\r\n- T&aacute;c dụng giảm thiểu nguy cơ rối loạn ti&ecirc;u h&oacute;a, lo&eacute;t dạ d&agrave;y, t&aacute; tr&agrave;ng, đại tr&agrave;ng.<br />\r\n<br />\r\n- T&aacute;c dụng chống oxy h&oacute;a, hỗ trợ điều trị khối u, ( u tử cung, u v&uacute;, tiền liệt tuyến....). Cao huyết &aacute;p, tăng mỡ m&aacute;u, đường m&aacute;u v&agrave; hỗ trợ an thần, tăng sức bền th&agrave;nh mạch,.<br />\r\n<br />\r\n- T&aacute;c dụng hỗ trợ tăng sức đề kh&aacute;ng, khả năng miễn dịch, hỗ trợ chống dị ứng.</p>\r\n\r\n<p><strong>Th&agrave;nh phần:</strong><br />\r\n<br />\r\n- C&acirc;y ho&agrave;n ngọc (rễ, th&acirc;n, l&aacute;) 40%, C&uacute;c hoa 20%, Kim ng&acirc;n hoa 20%, C&acirc;y lược v&agrave;ng 20%<br />\r\n<br />\r\n<strong>C&aacute;ch d&ugrave;ng:</strong><br />\r\n<br />\r\n- C&aacute;ch 1: mỗi lần cho 1 t&uacute;i tr&agrave; h&atilde;m v&agrave;o cốc nước s&ocirc;i, sau 3&gt;5 ph&uacute;t l&agrave; uống được<br />\r\n<br />\r\n- C&aacute;ch 2: cho 2 t&uacute;i lọc v&agrave;o b&igrave;nh đun s&ocirc;i từ 10&gt;15 ph&uacute;t l&agrave; d&ugrave;ng được, c&oacute; thể uống thay nước h&agrave;ng ng&agrave;y.<br />\r\n<br />\r\nKhối lượng: 120g/ hộp (30 t&uacute;i x 4g)</p>\r\n', '2016-12-01 17:14:54', 0, 0, 0, 1),
(23, 'Trà cung đình Huế', 85000, 200, 'tra cung dinh hue.jpg', 3, '<p><strong>C&ocirc;ng dụng của Tr&agrave;</strong></p>\r\n\r\n<p>Trong th&agrave;nh phần của Tr&agrave; Cung Đ&igrave;nh Huế bao gồm rất nhiều loại thảo dược,v&iacute; như: Atis&ocirc;, C&uacute;c hoa, Cỏ ngọt, Ho&agrave;i sơn, Đẳng s&acirc;m, Đại t&aacute;o, Hồng t&aacute;o,Hồi hoa, Cam thảo Bắc, Hoa l&agrave;i, Hoa h&ograve;e, Thảo quyết minh, Khổ qua, Kỷtử, Vối nụ, Tim sen. Mỗi vi thảo dược c&oacute; một chức năng một c&ocirc;ng dụngri&ecirc;ng, t&aacute;c dụng đến từng bộ phận của cơ thể. Khi tinh chế lại với nhautheo một b&iacute; quyết gia truyền sẽ tạo ra một sản phẩm độc nhất v&ocirc; nhị vềc&ocirc;ng dụng, bao gồm:<br />\r\n* Hỗ trợ điều trị chứng cao huyết &aacute;p, đau đầu, tim hồi hộp, mất ngủ.<br />\r\n* Gi&uacute;p tăng cường sức đề kh&aacute;ng, giảm cholestorol.<br />\r\n* Bổ khi huyết, thanh nhiệt, giảm độc, m&aacute;t gan, đẹp da, hết mụn.<br />\r\nNgo&agrave;i ra, sản phẩm n&agrave;y c&ograve;n tốt cho những người mắt yếu, tiểu đường, sỏi thận. Đặc biệt rất th&iacute;ch hợp với phụ nữ.</p>\r\n\r\n<p><strong>C&Aacute;CH THƯỞNG THỨC</strong>:&nbsp; Trộnđều g&oacute;i tr&agrave;, cho 30g Tr&agrave; th&ecirc;m 2-3 tr&aacute;i t&aacute;o v&agrave;o b&igrave;nh, đổ nước s&ocirc;i, tr&aacute;ngnước đầu. Ch&acirc;m nước s&ocirc;i v&agrave;o, 5 ph&uacute;t sau sẽ cho ra một b&igrave;nh Tr&agrave; CungĐ&igrave;nh Nhất Dạ Đế Vương để thưởng thức. C&oacute; thể bỏ ấm nấu s&ocirc;i, để nguội,đ&oacute;ng chai, bỏ tủ lạnh uống h&agrave;ng ng&agrave;y, m&ugrave;a h&egrave; th&ecirc;m đ&aacute;.<br />\r\n<strong>BẢO QUẢN</strong>: Để Tr&agrave; nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>Danh tr&agrave; Đức Phượng xin c&oacute; đ&ocirc;i lời t&acirc;mgiao: Trải qua bao thăng trầm của thời gian, mỗi ch&uacute;ng ta ai cũng trảiqua một lần uống một ly tr&agrave; n&oacute;ng hoặc lạnh. Ng&agrave;y xưa c&oacute; tr&agrave; Bắc Th&aacute;i,tr&agrave; m&oacute;c c&acirc;u, tr&agrave; sen, tr&agrave; l&agrave;i. Mỗi loại tr&agrave; c&oacute; một đặc t&iacute;nh ri&ecirc;ng củan&oacute;. Tr&agrave; Bắc Th&aacute;i, tr&agrave; m&oacute;c c&acirc;u khi uống v&ocirc; th&igrave; kh&oacute; ngủ. Tr&agrave; ướp xen ướpl&agrave;i uống v&ocirc;, hương vị đ&acirc;u phải l&agrave; hương tự nhi&ecirc;n, nếu c&oacute; th&igrave; rất &iacute;t. V&agrave;ng&agrave;y nay, khi tr&agrave; t&uacute;i lọc ra đời th&igrave; ta đ&acirc;u c&oacute; thể quan s&aacute;t được đ&oacute; l&agrave;nguy&ecirc;n liệu g&igrave; m&agrave; người ta say ra.<br />\r\nC&ograve;n b&acirc;y giờ, nếu bạn pha một b&igrave;nh Tr&agrave; Cung Đ&igrave;nh v&agrave;o một b&igrave;nh thuỷ tinh,khi đ&oacute; bạn sẽ cảm thấy r&otilde; r&agrave;ng c&aacute;i tinh hoa của n&oacute;. Những b&ocirc;ng hoa c&uacute;c,hoa l&agrave;i xo&egrave; nở, những tr&aacute;i hồng t&aacute;o, đại t&aacute;o khoe m&agrave;u v&agrave; khi thưởng thứcc&aacute;i vị ngọt lịm thanh tao nơi đầu m&ocirc;i ch&oacute;t lưỡi. &Ocirc;i tuyệt vời l&agrave;m sao!</p>\r\n', '2016-12-01 17:16:29', 0, 0, 0, 1),
(24, 'Nụ vối Hải Hậu', 130000, 186, 'nu voi hai hau.jpg', 2, '<p>Bạn c&oacute; biết nụ vối , l&aacute; vối những sản phẩm cực k&igrave; gần gũi gắn liền với cuộc sống ch&uacute;ng ta .Đặc biệt đối với c&aacute;c bạn ở l&agrave;ng qu&ecirc;.Nhưng kh&ocirc;ng hẳn ch&uacute;ng ta đều biết t&aacute;c dụng của sản phẩm n&agrave;y. V&agrave; Đặc biệt đối với sức khỏe con người :<br />\r\n<br />\r\n+ &quot;Thần dược&quot; cho bệnh ti&ecirc;u h&oacute;a<br />\r\n+Ph&ograve;ng v&agrave; điều trị tiểu đường nhờ nụ vối<br />\r\n+Chống &ocirc; xy h&oacute;a cho cơ thể<br />\r\n+Gi&uacute;p đ&agrave;o thải chất độc<br />\r\n+Thuốc s&aacute;t khuẩn cho da<br />\r\nC&aacute;ch sử dụng rất đơn giản:<br />\r\nL&aacute; vối kh&ocirc; rửa sạch cho v&agrave;o ấm, cho nước lạnh v&agrave;o đun đến s&ocirc;i rồi uống n&oacute;ng hoặc uống lạnh. Nụ vối cũng được đun trong nước đến khi s&ocirc;i hoặc, h&atilde;m trong nước s&ocirc;i như c&aacute;ch h&atilde;m tr&agrave; xanh.</p>\r\n', '2016-12-01 17:17:58', 0, 0, 14, 1);
INSERT INTO `products` (`pid`, `name`, `price`, `quantity`, `image`, `packing_method`, `description`, `created_at`, `rating`, `rating_times`, `sold`, `oeid`) VALUES
(25, 'Bí ngô', 108000, 200, 'bi ngo.jpg', 1, '<p><strong>1. T&ecirc;n khoa học:&nbsp;</strong>Cucurbita pepo L.</p>\r\n\r\n<p><strong>2. Họ:</strong>&nbsp;Bầu b&iacute; (Cucurbitaceae).</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:</strong>&nbsp;B&iacute; đỏ, B&ugrave; rợ.</p>\r\n\r\n<p><strong>4. M&ocirc; tả:</strong></p>\r\n\r\n<p>C&acirc;y thảo, sống l&acirc;u năm. Th&acirc;n c&oacute; năm cạnh, c&oacute; l&ocirc;ng dầy, thường c&oacute; rễ ở những đốt. L&aacute; mọc so le, c&oacute; cuống d&agrave;i 8-20 cm, phiến l&aacute; mềm, h&igrave;nh trứng rộng hoặc gần tr&ograve;n, chia th&ugrave;y n&ocirc;ng, đầu tr&ograve;n hoặc hơi nhọn, m&eacute;p c&oacute; răng cưa, hai mặt l&aacute; c&oacute; nhiều l&ocirc;ng mềm, đ&ocirc;i khi c&oacute; những đốm trắng ở mặt tr&ecirc;n ; tua cuốn ph&acirc;n nh&aacute;nh. Hoa đơn t&iacute;nh c&ugrave;ng gốc, mầu v&agrave;ng ; hoa đực c&oacute; đế hoa ngắn ; đ&agrave;i loe rộng c&oacute; th&ugrave;y h&igrave;nh dải hoặc gần dạng l&aacute;, tr&agrave;ng hoa c&oacute; 5 th&ugrave;y rộng ; hoa c&aacute;i c&oacute; l&aacute; đ&agrave;i dạng l&aacute; r&otilde;,&nbsp; bầu h&igrave;nh tr&ograve;n hoặc hơi d&agrave;i. Quả to, c&ugrave;i d&agrave;y, rỗng giữa c&oacute; nhiều dạng : dạng tr&ograve;n, hơi dẹt, c&oacute; r&atilde;nh s&acirc;u ; dạng h&igrave;nh trứng hoặc h&igrave;nh trứng hơi d&agrave;i, c&oacute; kh&iacute;a r&atilde;nh, vỏ ngo&agrave;i nhẵn, khi ch&iacute;n mầu v&agrave;ng trắng, vỏ giữa mầu v&agrave;ng cam, c&oacute; m&ugrave;i thơm, vị ngọt lợ, cuống quả c&oacute; r&atilde;nh v&agrave; loe rộng ở chỗ tiếp gi&aacute;p với quả ; hạt mầu trắng x&aacute;m, c&oacute; m&eacute;p mỏng v&agrave; mầu sẫm hơn. M&ugrave;a hoa : th&aacute;ng 3-4 ; m&ugrave;a quả th&aacute;ng 5-6</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>C&acirc;y được trồng nhiều nơi l&agrave;m thực phẩm.</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p>C&aacute;c v&ugrave;ng chuy&ecirc;n canh</p>\r\n\r\n<p><strong>7. Bộ phận d&ugrave;ng:</strong></p>\r\n\r\n<p>Hạt quả b&iacute; ng&ocirc; đ&atilde; gi&agrave;, ch&iacute;n v&agrave; đ&atilde; chế biến kh&ocirc; (Semen Cucurbitae).</p>\r\n\r\n<p><strong>8. Thu h&aacute;i, chế biến:</strong></p>\r\n\r\n<p>Khi c&acirc;y được 2-3 l&aacute; ch&iacute;nh thức, cắt ngọn, mỗi nh&aacute;nh n&ecirc;n giữ 2-3 quả để c&oacute; quả to, cắt bỏ phần ngọn sau quả thứ 3. &ETH;ể l&agrave;m rau ăn, c&oacute; thể d&ugrave;ng quả non hay quả gi&agrave;. &ETH;ể l&agrave;m thuốc, ta thu h&aacute;i quả gi&agrave;, lấy thịt quả d&ugrave;ng tươi, c&ograve;n hạt c&oacute; thể d&ugrave;ng tươi hay phơi kh&ocirc; d&ugrave;ng dần.</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học:</strong></p>\r\n\r\n<p>Hạt b&iacute; ng&ocirc; c&oacute; c&aacute;c chất protid, lipid, glucid, c&aacute;c chất kho&aacute;ng P, Mg, Ca, K. Hoạt chất l&agrave; một alcaloid: cucurbitin trong ph&ocirc;i v&agrave; vỏ lụa (c&oacute; t&aacute;c dụng tẩy giun s&aacute;n).<br />\r\nC&ugrave;i quả b&iacute; ng&ocirc; c&oacute; c&aacute;c chất protid, lipid, glucid, c&aacute;c acid amin (arginin, adenin&hellip;) c&aacute;c chất kho&aacute;ng P, Na, K, Ca, Mg, Fe, Cu, As,&hellip; c&aacute;c vitamin B1, C, caroten.</p>\r\n\r\n<p><strong>10. C&ocirc;ng năng:</strong></p>\r\n\r\n<p>Tẩy giun s&aacute;n, ức chế sự ph&aacute;t triển của s&aacute;n m&aacute;ng c&ograve;n gọi Huyết hấp tr&ugrave;ng (schistosomiase). C&ugrave;i quả b&iacute; ng&ocirc; được coi như c&oacute; t&aacute;c dụng bổ n&atilde;o.</p>\r\n\r\n<p><strong>11. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>Chữa s&aacute;n.</p>\r\n\r\n<p><strong>12. C&aacute;ch d&ugrave;ng, liều lượng:</strong></p>\r\n\r\n<p>B&oacute;c hết vỏ cứng, để nguy&ecirc;n m&agrave;ng xanh ở trong, gi&atilde; nhỏ trộn với đường hoặc mật để ăn v&agrave;o l&uacute;c đ&oacute;i, sau khoảng 3 giờ uống thuốc tẩy muối, đi ngo&agrave;i trong một chậu nước ấm. Người lớn d&ugrave;ng khoảng 100g nh&acirc;n hạt. Trẻ con 3-4 tuổi d&ugrave;ng 30g; 5-10 tuổi d&ugrave;ng 75g.</p>\r\n', '2016-12-01 17:19:45', 0, 0, 0, 1),
(26, 'Bách hợp', 78000, 200, 'bach hop.jpg', 1, '<p><strong>1. T&ecirc;n khoa học:&nbsp;</strong>Lilium brownii var. colchester Wils.</p>\r\n\r\n<p><strong>2. Họ:</strong>&nbsp;H&agrave;nh (Liliaceae)</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:</strong>&nbsp;C&aacute;nh hoa li ly</p>\r\n\r\n<p><strong>4. M&ocirc; tả:</strong></p>\r\n\r\n<p>C&acirc;y thảo cao 0,5-1m, sống nhiều năm. Th&acirc;n h&agrave;nh to m&agrave;u trắng đục c&oacute; khi phớt hồng, gần h&igrave;nh cầu, vẩy nhẵn v&agrave; dễ gẫy. L&aacute; mọc so le, h&igrave;nh mắc thu&ocirc;n, m&eacute;p nguy&ecirc;n, d&agrave;i 2-15cm, rộng 0,5-3,5cm. Cụm hoa mọc ở đầu c&agrave;nh, gồm 2-6 hoa to, h&igrave;nh loa k&egrave;n, d&agrave;i 14-16cm, với 6 c&aacute;nh hoa m&agrave;u trắng hay hơi hồng. Quả nang 5-6cm c&oacute; 3 ngăn, chứa nhiều hạt nhỏ h&igrave;nh tr&aacute;i xoan.</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>C&acirc;y B&aacute;ch hợp&nbsp; mọc hoang ở một số v&ugrave;ng n&uacute;i cao nước ta. Vị thuốc chủ yếu nhập từ Trung Quốc.</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p><strong>7. Bộ phận d&ugrave;ng:</strong></p>\r\n\r\n<p>Vẩy đ&atilde; chế biến kh&ocirc; của c&acirc;y B&aacute;ch hợp (Lilium brownii var. colchester Wils.), họ H&agrave;nh (Liliaceae).</p>\r\n\r\n<p><strong>8. Thu h&aacute;i, chế biến:</strong></p>\r\n\r\n<p>Sau một năm thu hoạch thường người ta ngắt hết hoa để cho củ to. Thu hoạch củ v&agrave;o cuối m&ugrave;a h&egrave;, đầu thu, khi c&acirc;y bắt đầu kh&ocirc; h&eacute;o. &ETH;&agrave;o về rửa sạch, t&aacute;ch ri&ecirc;ng từng vẩy, nh&uacute;ng nước s&ocirc;i 5-10 ph&uacute;t cho vừa ch&iacute;n t&aacute;i, rồi đem phơi hay sấy kh&ocirc;.</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học:</strong></p>\r\n\r\n<p>Tinh bột (30%), protid (4%), lipid (0,1%), vitamin C, alcaloid.</p>\r\n\r\n<p><strong>10. C&ocirc;ng năng:</strong></p>\r\n\r\n<p>Nhuận phế, giảm ho, định t&acirc;m, kiện vị, dưỡng trung ti&ecirc;u</p>\r\n\r\n<p><strong>11. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>Chữa ho nhiều do lao, thổ huyết, mệt mỏi, hồi hộp.</p>\r\n\r\n<p><strong>12. C&aacute;ch d&ugrave;ng, liều lượng:</strong></p>\r\n\r\n<p>Ng&agrave;y d&ugrave;ng 8-20g dạng thuốc sắc hoặc bột. Phối hợp trong c&aacute;c phương thuốc bổ phế chỉ kh&aacute;i, ho lao suy nhược. Dạng thuốc sắc hoặc ho&agrave;n, t&aacute;n.</p>\r\n', '2016-12-01 17:20:56', 0, 0, 0, 1),
(27, 'Bạch đậu khấu', 88000, 191, 'bach dau khau.jpg', 1, '<p><strong>1. T&ecirc;n khoa học:&nbsp;</strong>Amomum cardamomum L.</p>\r\n\r\n<p><strong>2. Họ:</strong>&nbsp;Gừng (Zingiberaceae).</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:&nbsp;</strong>Đậu khấu, vi&ecirc;n đậu khấu</p>\r\n\r\n<p><strong>4. M&ocirc; tả:</strong></p>\r\n\r\n<p>C&acirc;y: C&acirc;y thảo cao khoảng 2-3m. Th&acirc;n rễ nằm ngang to bằng ng&oacute;n tay, l&aacute; h&igrave;nh dải, mũi m&aacute;c, nhọn 2 đầu, d&agrave;i tới 55cm, rộng 6cm mặt tr&ecirc;n nhẵn, dưới c&oacute; v&agrave;i l&ocirc;ng rải r&aacute;c bẹ l&aacute; nhẵn, c&oacute; kh&iacute;a, lưỡi bẹ rất ngắn. Cụm hoa mọc ở gốc của th&acirc;n mang l&aacute;, mọc b&ograve;, d&agrave;i khoảng 40cm, mảnh, nhẵn, bao bởi nhiều vảy chuyển dần th&agrave;nh l&aacute; bắc ở ph&iacute;a tr&ecirc;n, l&aacute; bắc mau rụng. Cuống chung của cụm hoa ngắn, mang 3-5 hoa, ở n&aacute;ch những l&aacute; bắc nhỏ h&igrave;nh tr&aacute;i soan. Hoa m&agrave;u trắng t&iacute;m, c&oacute; cuống ngắn, đ&agrave;i h&igrave;nh ống nhẵn, c&oacute; 3 răng ngắn. Tr&agrave;ng h&igrave;nh ống nhẵn, d&agrave;i hơn đ&agrave;i 2 lần, th&ugrave;y h&igrave;nh tr&aacute;i xoan t&ugrave;, th&ugrave;y giữa hơi d&agrave;i rộng hơn, l&otilde;m hơn. C&aacute;nh m&ocirc;i h&igrave;nh thoi. Quả nang h&igrave;nh trứng, bao bởi đ&agrave;i tồn tại, c&oacute; khi lớn đến 4cm.<br />\r\nDược liệu: Quả h&igrave;nh cầu dẹt, c&oacute; 3 m&uacute;i, đường k&iacute;nh 1 - 1,5 cm. Mặt ngo&agrave;i vỏ m&agrave;u trắng, c&oacute; một số đường v&acirc;n dọc, đ&ocirc;i khi c&ograve;n s&oacute;t cuống quả. Vỏ quả kh&ocirc; dễ t&aacute;ch. Mỗi quả c&oacute; 20 - 30 hạt, gọi l&agrave; khấu mễ hoặc khấu nh&acirc;n, tập hợp th&agrave;nh khối h&igrave;nh cầu gọi l&agrave; khấu cầu. Khấu cầu chia l&agrave;m 3 m&uacute;i c&oacute; m&agrave;ng mỏng m&agrave;u trắng ngăn c&aacute;ch, mỗi m&uacute;i c&oacute; 7 - 10 hạt. H&igrave;nh dạng hạt kh&ocirc;ng đồng nhất, phần nhiều l&agrave; h&igrave;nh khối nhiều mặt kh&ocirc;ng đều, đường k&iacute;nh khoảng 3 mm, mặt ngo&agrave;i m&agrave;u n&acirc;u nhạt đến n&acirc;u thẫm, c&oacute; v&acirc;n nhỏ, chất cứng. Mặt cắt ngang m&agrave;u trắng. Hạt chứa nhiều tinh dầu. M&ugrave;i thơm, vị cay.</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>C&acirc;y mọc hoang, được trồng ở nước ta v&agrave; nhiều nước kh&aacute;c.</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p><strong>7. Bộ phận d&ugrave;ng:</strong></p>\r\n\r\n<p>Quả gần ch&iacute;n phơi kh&ocirc; của c&acirc;y Bạch đậu khấu (Amomum cardamomum L.), họ Gừng (Zingiberaceae).</p>\r\n\r\n<p><strong>8. Thu h&aacute;i, chế biến:</strong></p>\r\n\r\n<p>Thu h&aacute;i v&agrave;o m&ugrave;a thu, h&aacute;i c&acirc;y tr&ecirc;n 3 năm, h&aacute;i quả c&ograve;n giai đoạn xanh chuyển sang v&agrave;ng xanh. H&aacute;i về phơi trong r&acirc;m cho kh&ocirc;, c&oacute; khi phơi kh&ocirc; xong bỏ cuống rồi x&ocirc;ng di&ecirc;m sinh cho vỏ trắng cất d&ugrave;ng, khi d&ugrave;ng b&oacute;c vỏ lấy hạt.</p>\r\n\r\n<p>&nbsp;H&aacute;i quả khi c&oacute; m&agrave;u lục, bỏ cuống, phơi kh&ocirc;. Khi d&ugrave;ng bỏ vỏ quả lấy hạt, gi&atilde; n&aacute;t.</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học:</strong></p>\r\n\r\n<p>Tinh dầu trong đ&oacute; th&agrave;nh phần chủ yếu l&agrave; borneol v&agrave; camphor.</p>\r\n\r\n<p><strong>10. C&ocirc;ng năng:</strong></p>\r\n\r\n<p>Ho&aacute; thấp, h&agrave;nh kh&iacute;, ti&ecirc;u bĩ, khai vị, ti&ecirc;u thực, &ocirc;n trung</p>\r\n\r\n<p><strong>11. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>Chữa đau bụng lạnh, đầy hơi, n&ocirc;n oẹ, ăn kh&ocirc;ng ti&ecirc;u, ỉa chảy, tr&uacute;ng độc rượu.</p>\r\n\r\n<p><strong>12. C&aacute;ch d&ugrave;ng, liều lượng:</strong></p>\r\n\r\n<p>Ng&agrave;y 4-8g, dạng thuốc sắc hoặc bột.</p>\r\n', '2016-12-01 17:21:41', 0, 0, 9, 1),
(28, 'Bạch linh', 178000, 200, 'bach linh.jpg', 1, '<p><strong>1. T&ecirc;n khoa học:</strong>&nbsp;Poria cocos Wolf.</p>\r\n\r\n<p><strong>2. Họ:</strong>&nbsp;Nấm lỗ (Polyporaceae)</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:</strong>&nbsp;Bạch linh, phục linh</p>\r\n\r\n<p><strong>4. M&ocirc; tả:</strong></p>\r\n\r\n<p>Thể quả nấm Phục linh kh&ocirc;: h&igrave;nh cầu, h&igrave;nh thoi, h&igrave;nh cầu dẹt hoặc h&igrave;nh khối kh&ocirc;ng đều, lớn, nhỏ kh&ocirc;ng đồng nhất, mặt ngo&agrave;i m&agrave;u n&acirc;u đến n&acirc;u đen, c&oacute; nhiều vết nhăn r&otilde; v&agrave; lồi l&otilde;m. Thể nặng, rắn chắc. Mặt bẻ sần s&ugrave;i v&agrave; c&oacute; vết nứt, lớp viền ngo&agrave;i m&agrave;u n&acirc;u nhạt, phần trong m&agrave;u trắng, số &iacute;t c&oacute; m&agrave;u hồng nhạt. C&oacute; loại b&ecirc;n trong c&ograve;n mấy đoạn rễ th&ocirc;ng (Phục thần). Nấm phục linh kh&ocirc;ng m&ugrave;i, vị nhạt, cắn d&iacute;nh răng.</p>\r\n\r\n<p>Phục linh b&igrave;: L&agrave; lớp ngo&agrave;i Phục linh t&aacute;ch ra, lớn, nhỏ, kh&ocirc;ng đồng nhất. Mặt ngo&agrave;i từ n&acirc;u đến n&acirc;u đen, mặt trong m&agrave;u trắng hoặc n&acirc;u nhạt. Chất tương đối xốp, hơi c&oacute; t&iacute;nh đ&agrave;n hồi.</p>\r\n\r\n<p>Phục linh khối: sau khi t&aacute;ch lớp ngo&agrave;i, phần c&ograve;n lại được th&aacute;i, cắt th&agrave;nh phiến hay miếng, lớn nhỏ kh&ocirc;ng đồng nhất, m&agrave;u trắng, hồng nhạt hoặc n&acirc;u nhạt.<br />\r\nX&iacute;ch phục linh: L&agrave; lớp thứ hai sau lớp ngo&agrave;i, hơi hồng hoặc n&acirc;u nhạt.</p>\r\n\r\n<p>Bạch phục linh: L&agrave; phần b&ecirc;n trong, m&agrave;u trắng.</p>\r\n\r\n<p>Phục thần: L&agrave; phần nấm Phục linh &ocirc;m đoạn rễ th&ocirc;ng b&ecirc;n trong.</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>Một số rừng th&ocirc;ng ở v&ugrave;ng kh&iacute; hậu m&aacute;t của nước ta cũng c&oacute; loại nấm n&agrave;y nhưng chưa được nu&ocirc;i trồng v&agrave; khai th&aacute;c, vị thuốc chủ yếu nhập từ Trung.</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p><strong>7. Bộ phận d&ugrave;ng:</strong></p>\r\n\r\n<p>Dược liệu chia th&agrave;nh 4 thứ:</p>\r\n\r\n<p>Phục linh b&igrave; l&agrave; vỏ ngo&agrave;i.</p>\r\n\r\n<p>X&iacute;ch phục linh l&agrave; lớp thứ 2 sau vỏ ngo&agrave;i.</p>\r\n\r\n<p>Bạch phục linh l&agrave; phần b&ecirc;n trong m&agrave;u trắng, thường được sơ chế th&agrave;nh phiến h&igrave;nh khối vu&ocirc;ng dẹt.</p>\r\n\r\n<p>Phục thần l&agrave; những quả thể c&oacute; l&otilde;i gỗ (rễ th&ocirc;ng) ở giữa.</p>\r\n\r\n<p>Quốc.<br />\r\n<strong>8.&nbsp;</strong><strong>Thu h&aacute;i, chế biến:</strong></p>\r\n\r\n<p>Thu hoạch từ th&aacute;ng 7 đến th&aacute;ng 9, loại bỏ đất c&aacute;t, chất đống cho ra mồ h&ocirc;i rồi rải ra chỗ tho&aacute;ng gi&oacute; cho se bề mặt, tiếp tục chất đống, ủ v&agrave;i lần cho đến khi kh&ocirc; nước v&agrave; xuất hiện nhăn nheo bề mặt, phơi &acirc;m can đến kh&ocirc;. Hoặc Phục linh tươi th&aacute;i miếng v&agrave; phơi &acirc;m can nơi tho&aacute;ng gi&oacute;. Tuỳ theo c&aacute;c phần th&aacute;i v&agrave; m&agrave;u sắc của Phục linh m&agrave; c&oacute; t&ecirc;n gọi kh&aacute;c nhau: Bạch phục linh, Phục linh b&igrave;, X&iacute;ch phục linh, Phục linh khối, Phục linh phiến.</p>\r\n\r\n<p>Ng&acirc;m Phục linh v&agrave;o nước, rửa sạch, đồ th&ecirc;m cho mềm, gọt vỏ, th&aacute;i miếng hoặc th&aacute;i l&aacute;t l&uacute;c đang mềm v&agrave; phơi hoặc sấy kh&ocirc;.</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học:</strong>&nbsp;đường (trong đ&oacute; c&oacute; pachymose l&agrave; đường đặc hiệu), chất kho&aacute;ng, c&aacute;c hợp chất triterpenoid.</p>\r\n\r\n<p>C&ocirc;ng năng: thuốc lợi thuỷ v&agrave; cường tr&aacute;ng,&nbsp; nhuận t&aacute;o, bổ tỳ, &iacute;ch kh&iacute;&ugrave;, sinh t&acirc;n, chỉ kh&aacute;t.</p>\r\n\r\n<p><strong>10. T&aacute;c dụng dược l&yacute;:</strong></p>\r\n\r\n<p>Thuốc c&oacute; t&aacute;c dụng lợi tiểu, cũng c&oacute; b&aacute;o c&aacute;o cho l&agrave; t&aacute;c dụng lợi tiểu kh&ocirc;ng r&otilde;, c&oacute; thể do điều kiện nghi&ecirc;n cứu kh&aacute;c nhau.</p>\r\n\r\n<p>Thuốc c&oacute; t&aacute;c dụng tăng miễn dịch, tăng chỉ số thực b&agrave;o của phagocyte ở chuột.&nbsp;<br />\r\nThuốc c&oacute; t&aacute;c dụng kh&aacute;ng ung thư (do th&agrave;nh phần polysacharide của thuốc) do l&agrave;m tăng miễn dịch cơ thể.</p>\r\n\r\n<p>Thuốc c&oacute; t&aacute;c dụng an thần, c&oacute; t&aacute;c dụng hạ đường huyết, bảo vệ gan v&agrave; chống l&oacute;et bao tử.</p>\r\n\r\n<p>Nước sắc Phục linh c&oacute; t&aacute;c dụng ức chế đối với tụ cầu v&agrave;ng, trực khuẩn đại tr&agrave;ng, trực khuẩn biến dạng. Cồn ng&acirc;m kiệt thuốc c&oacute; t&aacute;c dụng giết chết xoắn khuẩn.<br />\r\n<strong>11.&nbsp;</strong><strong>C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>Phục linh b&igrave;: Lợi tiểu, trị ph&ugrave; thũng.</p>\r\n\r\n<p>X&iacute;ch phục linh: Chữa thấp nhiệt (chướng bụng, vi&ecirc;m b&agrave;ng quang, tiểu v&agrave;ng, đ&aacute;i rắt).</p>\r\n\r\n<p>Bạch phục linh: Chữa ăn uống k&eacute;m ti&ecirc;u, đầy chướng, b&iacute; tiểu tiện, ho c&oacute; đờm, ỉa chảy.<br />\r\nPhục thần: Trị yếu tim, hoảng sợ, hồi hộp, mất ngủ.</p>\r\n\r\n<p><strong>12. C&aacute;ch d&ugrave;ng, liều lượng:</strong></p>\r\n\r\n<p>Ng&agrave;y 6-12g. Dạng thuốc sắc, ho&agrave;n, t&aacute;n. Phối hợp trong nhiều phương thuốc kh&aacute;c nhau.</p>\r\n\r\n<p>Ki&ecirc;ng kỵ:&nbsp;&acirc;m hư m&agrave; kh&ocirc;ng thấp nhiệt th&igrave; kh&ocirc;ng n&ecirc;n d&ugrave;ng.</p>\r\n', '2016-12-01 17:23:19', 0, 0, 0, 1),
(29, 'Bạch thược', 98000, 198, 'bach thuoc.jpg', 1, '<p><strong>1. T&ecirc;n khoa học:</strong>&nbsp;Paeonia lactiflora Pall.</p>\r\n\r\n<p><strong>2. Họ:</strong>&nbsp;Ho&agrave;ng li&ecirc;n (Ranunculaceae).</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:&nbsp;</strong>Thược dược</p>\r\n\r\n<p><strong>4. M&ocirc; tả:</strong></p>\r\n\r\n<p>C&acirc;y: C&acirc;y thảo sống l&acirc;u năm, cao 50-80cm, l&aacute; mọc so le, chụm hai hay chụm ba, k&eacute;p, với 9-12 phần ph&acirc;n chia, c&aacute;c đoạn kh&ocirc;ng đều, h&igrave;nh tr&aacute;i xoan ngọn gi&aacute;o, d&agrave;i 8-12cm, rộng 2-4 cm m&eacute;p nguy&ecirc;n, ph&iacute;a cuống hơi hồng. Hoa rất to mọc đơn độc, c&oacute; m&ugrave;i hoa Hồng, tr&ecirc;n mỗi th&acirc;n mang hoa c&oacute; 1-7 hoa, rộng 10-12cm. Đ&agrave;i c&oacute; 6 phiến, c&aacute;nh hoa xếp tr&ecirc;n một d&atilde;y hoặc hai d&atilde;y, m&agrave;u hồng thịt trước khi nở, rồi chuyển dần sang m&agrave;u trắng tinh; bao phấn m&agrave;u da cam. Quả gồm 3-5 l&aacute; no&atilde;n. C&oacute; nhiều thứ trồng kh&aacute;c nhau, c&oacute; hoa c&oacute; độ lớn, số lượng c&aacute;nh hoa, m&agrave;u sắc... kh&aacute;c nhau. Hoa th&aacute;ng 5-6.</p>\r\n\r\n<p>Dược liệu : Rễ h&igrave;nh trụ tr&ograve;n, thẳng hoặc đ&ocirc;i khi hơi uốn cong, hai đầu phẳng; đều nhau hoặc một đầu to hơn, d&agrave;i 10 - 20 cm, đường k&iacute;nh 1 - 2,0 cm. Mặt ngo&agrave;i hơi trắng hoặc hồng nhạt, nhẵn hoặc đ&ocirc;i khi c&oacute; nếp nhăn dọc v&agrave; vết t&iacute;ch của rễ nhỏ. Đ&ocirc;i khi c&ograve;n vỏ ngo&agrave;i m&agrave;u n&acirc;u thẫm. Chất rắn chắc, nặng, kh&oacute; bẻ gẫy. Mặt cắt phẳng m&agrave;u trắng ng&agrave; hoặc hơi phớt hồng. M&ocirc; mềm vỏ hẹp, mạch gỗ xếp th&agrave;nh h&igrave;nh nan hoa xe đạp, kh&ocirc;ng c&oacute; m&ugrave;i, vị hơi đắng, hơi chua.<br />\r\nBộ phận d&ugrave;ng : Vị thuốc l&agrave; rễ đ&atilde; cạo bỏ lớp bần v&agrave; chế biến kh&ocirc; của c&acirc;y Thược dược (Paeonia lactiflora Pall.)</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>C&acirc;y được nhập giống từ Trung Quốc v&agrave;o trồng ở Sapa (L&agrave;o Cai). C&acirc;y được trồng nơi n&uacute;i cao, kh&iacute; hậu m&aacute;t, mọc dưới những c&acirc;y bụi hoặc c&acirc;y to.</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p>C&oacute; khả năng ph&aacute;t triển trồng ở c&aacute;c v&ugrave;ng chuy&ecirc;n canh</p>\r\n\r\n<p><strong>7. Bộ phận d&ugrave;ng:</strong></p>\r\n\r\n<p>Củ.&nbsp;D&ugrave;ng loại củ c&oacute; đường k&iacute;nh 1-2cm, d&agrave;i 10-15cm, m&agrave;u trắng hồng, &iacute;t xơ.</p>\r\n\r\n<p><strong>8. Thu h&aacute;i, chế biến:</strong></p>\r\n\r\n<p>Thu hoạch sau 4 năm, v&agrave;o khoảng th&aacute;ng 8-10. Đ&agrave;o l&ecirc;n, cắt bỏ th&acirc;n rễ v&agrave; rễ con, rửa sạch, ng&acirc;m nước 1-2 giờ, ủ 1-2 ng&agrave;y đ&ecirc;m (c&oacute; thể đồ), sau khi đồ sửa lại cho thẳng rồi phơi hay sấy kh&ocirc;. C&oacute; thể b&agrave;o hay th&aacute;i mỏng sau khi đồ rồi sao qua. C&oacute; khi tẩm giấm rồi sao qua hay sao ch&aacute;y cạnh, hoặc tẩm rượu sao qua. L&uacute;c chưa b&agrave;o chế cần phải sấy lưu huỳnh, sau khi b&agrave;o chế cần để nơi kh&ocirc; r&aacute;o, tr&aacute;nh ẩm.</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học:</strong></p>\r\n\r\n<p>Trong rễ c&oacute; paeoniflorin, oxypaeoniflorin, albiflorin, benzoyl-paeoniflorin, oxypaeoniflorinone, paeonolide, paeonol... c&ograve;n c&oacute; tinh bột, tanin, calci oxalat, tinh dầu, chất b&eacute;o, chất nhầy, acid benzoic.</p>\r\n\r\n<p><strong>10. C&ocirc;ng năng:</strong></p>\r\n\r\n<p>Liễm &acirc;m, dưỡng huyết, b&igrave;nh can, chỉ thống</p>\r\n\r\n<p><strong>11. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>D&ugrave;ng dạng sống chữa nhức đầu, ch&acirc;n tay đau nhức, trị tả lỵ, mồ h&ocirc;i trộm, tiểu tiện kh&oacute;, đ&aacute;i đường; giải nhiệt, chữa cảm mạo do chứng lo g&acirc;y n&ecirc;n.</p>\r\n\r\n<p>Dạng sao tẩm chữa c&aacute;c bệnh về huyết, th&ocirc;ng kinh nguyệt. Nếu sao ch&aacute;y cạnh chữa băng huyết. Nếu sao v&agrave;ng chữa đau bụng kinh, rong kinh&hellip;</p>\r\n\r\n<p><strong>12. C&aacute;ch d&ugrave;ng, liều lượng:</strong></p>\r\n\r\n<p>Ng&agrave;y d&ugrave;ng 6 -12g, dạng thuốc sắc.</p>\r\n', '2016-12-01 17:24:04', 5, 1, 2, 1),
(30, 'Bạc hà', 148000, 200, 'bac ha.jpg', 1, '<p><strong>1. T&ecirc;n khoa học</strong>: Mentha arvensis L. (Bạc h&agrave; &Aacute;), hoặc Mentha piperita L. (Bạc h&agrave; &Acirc;u),</p>\r\n\r\n<p><strong>2. Họ:&nbsp;</strong>Bạc h&agrave; (Lamiaceae). C&acirc;y được trồng ở nhiều địa phương nước ta.</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:</strong></p>\r\n\r\n<p><strong>4. M&ocirc; tả:</strong></p>\r\n\r\n<p>C&acirc;y thảo, sống l&acirc;u năm. Th&acirc;n mềm, h&igrave;nh vu&ocirc;ng. Loại th&acirc;n ngầm mang rễ mọc b&ograve; lan, loại th&acirc;n đứng mang l&aacute;, cao 30-40cm, c&oacute; khi hơn, mầu xanh lục hoặc t&iacute;m t&iacute;a. L&aacute; mọc đối, h&igrave;nh bầu dục hoặc h&igrave;nh trứng. Cuống ngắn. M&eacute;p l&aacute; kh&iacute;a răng đều. Hoa nhỏ, mầu trắng, hồng hoặc t&iacute;m hồng, mọc tụ tập ở kẽ l&aacute; th&agrave;nh những v&ograve;ng nhiều hoa. L&aacute; bắc nhỏ, h&igrave;nh d&ugrave;i. Đ&agrave;i h&igrave;nh chu&ocirc;ng c&oacute; 5 răng đều nhau. Tr&agrave;ng c&oacute; ống ngắn. Phiến tr&agrave;ng chia l&agrave;m 4 phần gần bằng nhau, c&oacute; 1 v&ograve;ng l&ocirc;ng ở ph&iacute;a trong. 4 nhụy bằng nhau, chi nhụy nhẵn. Quả bế c&oacute; 4 hạt. C&aacute;c bộ phận tr&ecirc;n mặt đất c&oacute; l&ocirc;ng gồm l&ocirc;ng che chở v&agrave; l&ocirc;ng b&agrave;i tiết tinh dầu.</p>\r\n\r\n<p>M&ugrave;a hoa quả v&agrave;o th&aacute;ng 7 - 10.</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>C&acirc;y của v&ugrave;ng &Acirc;u &Aacute; &ocirc;n đới. Ở nước ta c&oacute; những c&acirc;y mọc hoang ở miền n&uacute;i, nơi đất ẩm, m&aacute;t v&agrave; những chủng nhập trồng ở nhiều nơi..&nbsp;&nbsp;</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p>C&acirc;y ưa đất xốp, tốt nhất l&agrave; đất s&eacute;t c&oacute; nhiều m&ugrave;n, ẩm ướt, tho&aacute;t nước nhưng đủ độ ẩm. Đất cần l&agrave;m cỏ b&oacute;n ph&acirc;n kỹ trước, l&agrave;m luống rộng, tr&ecirc;n mỗi luống trồng 2-3 h&agrave;ng. C&oacute; thể trồng quanh năm nhưng thường trồng v&agrave;o 2 m&ugrave;a l&agrave; m&ugrave;a xu&acirc;n (th&aacute;ng 2-3) v&agrave; m&ugrave;a thu (th&aacute;ng 8-9), trồng v&agrave;o m&ugrave;a xu&acirc;n cho năng suất cao nhất. Trồng bằng hạt (&iacute;t &aacute;p dụng), th&acirc;n ngầm hoặc th&acirc;n c&acirc;y tr&ecirc;n mặt đất, cắt đoạn d&agrave;i 15-30cm. Sau 3-4 th&aacute;ng c&oacute; thể thu hoạch đợt đầu, thường một năm c&oacute; thể cắt c&acirc;y 3-4 lần; lần thứ nhất v&agrave;o th&aacute;ng 6-7, sau đ&oacute; cần xới v&agrave; b&oacute;n ph&acirc;n, sau 2 th&aacute;ng (v&agrave;o cuối th&aacute;ng 8 hay th&aacute;ng 9) lại h&aacute;i lần nữa v&agrave;o l&uacute;c c&acirc;y đang ra hoa nhiều. H&aacute;i về, cần b&oacute; lại từng b&oacute;, phơi chỗ m&aacute;t cho kh&ocirc;, hoặc nếu cất tinh dầu th&igrave; cần cất ngay hoặc để hơi h&eacute;o rồi cất.</p>\r\n\r\n<p><strong>7. Bộ phận d&ugrave;ng:</strong></p>\r\n\r\n<p>Th&acirc;n, c&agrave;nh mang l&aacute; (Herba Menthae)</p>\r\n\r\n<p><strong>8. Thu h&aacute;i, chế biến:</strong></p>\r\n\r\n<p>Thu h&aacute;i v&agrave;o khoảng th&aacute;ng 5, 8, 11 thu hoạch l&uacute;c c&acirc;y mới ra hoa, rửa sạch d&ugrave;ng tươi hoặc phơi trong r&acirc;m cho kh&ocirc;.</p>\r\n\r\n<p>Lấy l&aacute; Bạc h&agrave; kh&ocirc;, tẩm nước, để v&agrave;o chỗ r&acirc;m m&aacute;t, khi l&aacute; c&acirc;y mềm, cắt ngắn từng đoạn, phơi trong r&acirc;m cho kh&ocirc; để d&ugrave;ng (Trung Dược Đại Từ Điển).</p>\r\n\r\n<p>Rửa qua, để r&aacute;o nước, cắt ngắn chừng 2cm, phơi trong r&acirc;m cho kh&ocirc; (Dược Liệu Việt&nbsp;Nam).</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học ch&iacute;nh:</strong></p>\r\n\r\n<p>Tinh dầu, trong đ&oacute; th&agrave;nh phần chủ yếu l&agrave; menthol.</p>\r\n\r\n<p><strong>10. T&aacute;c dụng dược l&yacute;:</strong></p>\r\n\r\n<p>T&aacute;c dụng kh&aacute;ng khuẩn: nước sắc Bạc h&agrave; c&oacute; t&aacute;c dụng ức chế đối với virus ECHO v&agrave; Salmonella Typhoit (Trung Dược Học).</p>\r\n\r\n<p>T&aacute;c dụng tr&ecirc;n cơ trơn: Menthol v&agrave; Menthone c&oacute; t&aacute;c dụng ức chế tr&ecirc;n ruột thỏ, Menthone c&oacute; t&aacute;c dụng mạnh hơn (Trung Dược Học).</p>\r\n\r\n<p>Ức chế đau: Tinh dầu Bạc h&agrave; v&agrave; Menthol bốc hơi nhanh, g&acirc;y cảm gi&aacute;c m&aacute;t v&agrave; t&ecirc; tại chỗ, d&ugrave;ng trong trường hợp đau d&acirc;y thần kinh (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p>S&aacute;t khuẩn mạnh: d&ugrave;ng trong những trường hợp ngứa của 1 số bệnh ngo&agrave;i da, bệnh về tai, mũi, họng (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p>Ức chế h&ocirc; hấp, tuần ho&agrave;n: đối với trẻ em &iacute;t tuổi, tinh dầu Bạc h&agrave; v&agrave; Menthol b&ocirc;i v&agrave;o mũi hoặc cổ họng c&oacute; thể g&acirc;y hiện tượng ức chế dẫn tới ngừng thở v&agrave; tim ngưng đập ho&agrave;n to&agrave;n. Người ta đ&atilde; nhận x&eacute;t thấy 1 số trường hợp chết do nhỏ mũi 1 giọt dầu Menthol 1% hoặc b&ocirc;i v&agrave;o ni&ecirc;m mạc mũi loại thuốc mỡ c&oacute; Menthol. V&igrave; vậy, cần hết sức thận trọng khi d&ugrave;ng tinh dầu Bạc h&agrave; hoặc dầu c&ugrave; l&agrave; cho trẻ nhỏ &iacute;t tuổi, nhất l&agrave; trẻ mới đẻ (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc ViệtNam).</p>\r\n\r\n<p>T&aacute;c động đến nhiệt độ cơ thể: Bạc h&agrave;, tinh dầu Bạc h&agrave; hoặc Menthol uống với liều rất nhỏ c&oacute; thể g&acirc;y hưng phấn, l&agrave;m tăng b&agrave;i tiết của tuyến mồ h&ocirc;i, l&agrave;m nhiệt độ cơ thể hạ thấp (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt Nam).</p>\r\n\r\n<p>Liều lớn c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch tủy sống, g&acirc;y t&ecirc; liệt phản xạ v&agrave; ngăn cản sự l&ecirc;n men b&igrave;nh thường trong ruột (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p>Bạc h&agrave; c&oacute; t&aacute;c dụng kh&aacute;ng vi khuẩn trong th&iacute; nghiệm In Vitro đối với c&aacute;c chủng vi khuẩn tả Vibrio Choreia Elto, Vibrio Choreia Inaba, Vibrio Choreia Ogawa (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p>Tinh dầu Bạc h&agrave; c&oacute; t&aacute;c dụng ức chế thần kinh trung ương do t&aacute;c dụng chủ yếu của Menthol (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p>Tinh dầu Bạc h&agrave; l&agrave;m giảm sự vận động v&agrave; chống co thắt của ruột non. C&aacute;c chất Menthol v&agrave; Menthone ức chế sự vận động của đường ti&ecirc;u h&oacute;a từ ruột xuống, c&oacute; t&aacute;c dụng l&agrave;m gĩan mao mạch (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p><strong>11. C&ocirc;ng năng:</strong></p>\r\n\r\n<p>T&aacute;n phong nhiệt, hạ sốt, th&ocirc;ng mũi, chữa nhức đầu, gi&uacute;p ti&ecirc;u h&oacute;a, đau bụng</p>\r\n\r\n<p><strong>12. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>Chữa cảm c&uacute;m, nhức đầu, ngạt mũi, vi&ecirc;m họng, k&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute;, chữa đau bụng, đầy bụng.</p>\r\n\r\n<p>Cất tinh dầu bạc h&agrave; v&agrave; chế menthol d&ugrave;ng để sản xuất dầu cao sao v&agrave;ng, l&agrave;m chất thơm cho c&aacute;c sản phẩm thực phẩm như b&aacute;nh kẹo, thuốc đ&aacute;nh răng, v&agrave; trong một số ng&agrave;nh kỹ nghệ kh&aacute;c.</p>\r\n\r\n<p><strong>13. C&aacute;ch d&ugrave;ng, liều lượng:</strong></p>\r\n\r\n<p>D&ugrave;ng dưới dạng thuốc x&ocirc;ng, thuốc h&atilde;m 12-20g mỗi ng&agrave;y.</p>\r\n', '2016-12-01 17:24:59', 0, 0, 0, 1),
(31, 'Ba kích', 68000, 199, 'ba kich.jpg', 1, '<p><strong>1. T&ecirc;n khoa học:</strong>&nbsp;Morinda officinalis How.</p>\r\n\r\n<p><strong>2. Họ:</strong>&nbsp;C&agrave; ph&ecirc; - Rubiaceae.</p>\r\n\r\n<p><strong>3. T&ecirc;n kh&aacute;c:</strong>&nbsp;D&acirc;y ruột g&agrave;, ba k&iacute;ch thi&ecirc;n, li&ecirc;n ch&acirc;u ba k&iacute;ch, chẩu ph&oacute;ng x&igrave;, s&aacute;y c&aacute;y (Th&aacute;i), thau t&agrave;y c&aacute;y (T&agrave;y), chồi ho&agrave;ng kim, ch&agrave;y ki&agrave;ng đ&ograve;i (Dao).</p>\r\n\r\n<p><strong>4. M&ocirc; tả thực vật:</strong></p>\r\n\r\n<p>D&acirc;y leo, sống nhiều năm, th&acirc;n non m&agrave;u t&iacute;m, c&oacute; l&ocirc;ng, c&agrave;nh non c&oacute; cạnh. L&aacute; mọc đối, cứng nhọn, h&igrave;nh ngọn gi&aacute;o thu&ocirc;n d&agrave;i 6-14cm, rộng 2,5-6cm, l&uacute;c non m&agrave;u xanh, c&oacute; l&ocirc;ng d&agrave;i ở mặt dưới, sau đ&oacute; &iacute;t l&ocirc;ng v&agrave; c&oacute; m&agrave;u trắng mốc; l&aacute; k&egrave;m h&igrave;nh ống. Hoa nhỏ, m&agrave;u trắng sau hơi v&agrave;ng, 2-10 c&aacute;nh hoa, 4 nhị, mọc th&agrave;nh cụm ở kẽ l&aacute;, đầu c&agrave;nh. Quả tr&ograve;n, khi ch&iacute;n m&agrave;u đỏ. Tr&aacute;nh nhầm với c&acirc;y ba k&iacute;ch l&ocirc;ng (M. cochinchinensis DC.) v&agrave; c&acirc;y mặt quỷ (M. villosa Hook.).</p>\r\n\r\n<p>M&ugrave;a hoa: th&aacute;ng 5-6, m&ugrave;a quả: th&aacute;ng 7-10.</p>\r\n\r\n<p><strong>5. Ph&acirc;n bố:</strong></p>\r\n\r\n<p>C&acirc;y mọc hoang ở v&ugrave;ng rừng thứ sinh, trung du v&agrave; miền n&uacute;i c&aacute;c tỉnh ph&iacute;a Bắc, dưới t&aacute;n một số kiểu rừng nhiệt đới ẩm l&aacute; rộng thường xanh,nay trở n&ecirc;n thứ sinh gồm c&acirc;y bụi v&agrave; d&acirc;y leo chằng chịt hoặc ở bờ nương rẫy. Độ cao ph&acirc;n bố khoảng 100m so với mặt biển. C&agrave;ng l&ecirc;n cao c&acirc;y c&agrave;ng thưa dần, đến độ cao khoảng 100m th&igrave; hầu như hiếm gặp. C&oacute; nhiều nhất ở c&aacute; tỉnh Quảng Ninh, Ph&uacute; Thọ, Ho&agrave; B&igrave;nh, H&agrave; Giang, Lạng Sơn.</p>\r\n\r\n<p><strong>6. Trồng trọt:</strong></p>\r\n\r\n<p>Ba k&iacute;ch ưa s&aacute;ng, ưa ẩm v&agrave; chịu b&oacute;ng, mọc tr&ecirc;n đất feralit đỏ v&agrave;ng, c&oacute; lượng m&ugrave;n ở mức trung b&igrave;nh, tơi xốp v&agrave; hơi chua, nhiệt độ kh&ocirc;ng kh&iacute; trung b&igrave;nh năm khoảng 21-23oC.</p>\r\n\r\n<p>Trồng bằng rễ, c&agrave;nh b&aacute;nh tẻ hay gieo ươm bằng hạt.</p>\r\n\r\n<p>V&agrave;o khoảng th&aacute;ng 3-4, khi thời tiết ấm dần, chọn những c&agrave;nh b&aacute;nh tẻ ở c&acirc;y Ba k&iacute;ch sống khoẻ, chặt th&agrave;nh từng đoạn 20-30cm, mỗi đoạn c&oacute; 2-4 mắt. C&oacute; thể đem trồng ngay hoặc gi&acirc;m v&agrave; vườn ươm cho đến khi nảy mầm, ra rễ mới đem trồng.</p>\r\n\r\n<p>Chọn c&aacute;c ruộng cao s&aacute;t ch&acirc;n n&uacute;i hay đất nương rẫy đ&atilde; ph&aacute;t quang, đất c&ograve;n m&agrave;u mỡ, luon ẩm. Cuốc s&acirc;u, để ải, l&agrave;m nhỏ đất, đ&aacute;nh luống cao 20-30cm, rộng 40cm nếu trồng th&agrave;nh 1 h&agrave;ng hoặc rộng hơn nếu trồng th&agrave;nh 2 h&agrave;ng. Tr&ecirc;n luống cuốc th&agrave;nh c&aacute;c hố s&acirc;u 15cm, c&aacute;ch nhau 60cm (trồng được khoảng 30.000 kh&oacute;m/ha). B&oacute;n l&oacute;t ph&acirc;n chuồng mục (10 tấn/ha), trộn đều. Kh&ocirc;ng d&ugrave;ng ph&acirc;n tươi v&igrave; c&oacute; thể l&agrave;m thối rễ. Mặc d&ugrave; Ba k&iacute;ch l&agrave; c&acirc;y ưa s&aacute;ng nhưng ở thời kỳ mới trồng, c&acirc;y lại ưa b&oacute;ng. V&igrave; vậy cần l&agrave;m gi&agrave;n che cho từng luống, cao 0,7-1m, ở tr&ecirc;n che bằng ph&ecirc;n hay cỏ tranh, để lượng &aacute;nh s&aacute;ng lọt qua khoảng 50%.</p>\r\n\r\n<p>Thời gian trồng th&iacute;ch hợp từ th&aacute;ng 3-4. Hom giống cắt xong đem xử l&yacute; ngay, mỗi hốc 2-3 hom. Hom đặt nghi&ecirc;ng lấp đất chặt chỉ để hở 1-3cm. Nếu khong c&oacute; gi&agrave;n che, đặt xong phải phủ ngay bằng c&acirc;y tế, guột hoặc r&aacute;c. Thường xuy&ecirc;n tưới nước, giữ cho đất ẩm. Hom nảy mầm sau 10-15 ng&agrave;y, tỷ lệ khoảng 60%.</p>\r\n\r\n<p>Ba k&iacute;ch sinh trưởng&nbsp;&nbsp;nhanh, chỉ sau 6-7 th&aacute;ng trồng đ&atilde; c&oacute; thể vượt l&ecirc;n tr&ecirc;n gi&agrave;n che, vươn l&ecirc;n sống trong điều kiện chiếu s&aacute;ng ho&agrave;n to&agrave;n. L&uacute;c n&agrave;y cần l&agrave;m gi&aacute; thể cho c&acirc;y. Mỗi năm chỉ cần l&agrave;m cỏ v&agrave; vun xới 1 lần. V&agrave;o thời kỳ hạn h&aacute;n cần tưới th&ecirc;m nước.</p>\r\n\r\n<p><strong>7. Thu h&aacute;i chế biến:</strong></p>\r\n\r\n<p>Ba k&iacute;ch trồng được 3 năm c&oacute; thể thu hoạch. Năng suất dược liệu l&agrave; rễ phụ thuộc v&agrave;o số năm trồng. Th&ocirc;ng thường để nhiều năm, năng suất c&agrave;ng cao. Thời gian thu hoạch thường v&agrave;o th&aacute;ng 10-11. Cũng c&oacute; thể v&agrave;o m&ugrave;a xu&acirc;n để tận dụng lấy giống trồng ngay.</p>\r\n\r\n<p>D&ugrave;ng cuốc đ&agrave;o rộng xung quanh gốc, lấy to&agrave;n bộ rễ, rửa sạch đất c&aacute;t. Phơi nắng liền 6-7 ng&agrave;y, sau đập nhẹ l&agrave;m bẹt phần thịt rễ, tr&aacute;nh đập mạnh l&agrave;m n&aacute;t rễ. Sau đ&oacute; lại phơi đến kh&ocirc;. Cắt th&agrave;nh từng đoạn d&agrave;i 10cm, r&uacute;t bỏ l&otilde;i, đựng trong bao c&oacute;i. Để nơi kh&ocirc; r&aacute;o, tho&aacute;ng gi&oacute;, cần phải x&ocirc;ng lưu huỳnh ngay trong kho để tr&aacute;nh mốc mọt.</p>\r\n\r\n<p><strong>8. Bộ phận d&ugrave;ng:</strong>&nbsp;Rễ phơi hay sấy kh&ocirc; của c&acirc;y Ba k&iacute;ch (Morinda officinalis How.), họ C&agrave; ph&ecirc; (Rubiaceae).</p>\r\n\r\n<p><strong>9. Th&agrave;nh phần ho&aacute; học:</strong></p>\r\n\r\n<p>Rễ tươi chứa chất đường, nhựa, acid hữu cơ, vitamin C, tinh dầu, anthraglycosid, phytosterol. Nhưng trong ba k&iacute;ch kh&ocirc; kh&ocirc;ng thấy c&oacute; vitamin C.</p>\r\n\r\n<p><strong>10. T&aacute;c dụng dược l&yacute;:</strong></p>\r\n\r\n<p>10.1. Tăng sức dẻo dai: Với phương ph&aacute;p&nbsp; chuột bơi, Ba K&iacute;ch với liều 5-10g/kg d&ugrave;ng li&ecirc;n tiếp 7 ng&agrave;y thấy c&oacute; t&aacute;c dụng tăng sức dẻo dai cho s&uacute;c vật th&iacute; nghiệm (Trung Dược Học).</p>\r\n\r\n<p>10.2. Tăng&nbsp; sức đề kh&aacute;ng: d&ugrave;ng phương ph&aacute;p&nbsp; g&acirc;y nhiễm độc cấp bằng Ammoni Clorua tr&ecirc;n chuột nhắt trắng, với liều 15g/kg, Ba K&iacute;ch c&oacute; t&aacute;c dụng tăng cường sức đề kh&aacute;ng chung của cơ thể đối với c&aacute;c yếu tố độc hại (Trung Dược Học).</p>\r\n\r\n<p>10.3. Chống vi&ecirc;m: Tr&ecirc;n m&ocirc; h&igrave;nh g&acirc;y vi&ecirc;m thực nghiệm ở chuột cống trắng bằng Kaolin với liều lượng 5-10g/kg, Ba K&iacute;ch c&oacute; t&aacute;c dụng chống vi&ecirc;m r&otilde; rệt (Trung Dược Học).</p>\r\n\r\n<p>10.4. Đối với hệ thống nội tiết: th&iacute; nghiệm tr&ecirc;n chuột lớn v&agrave; chuột nhắt cho thấy Ba K&iacute;ch kh&ocirc;ng c&oacute; t&aacute;c dụng kiểu Androgen nhưng c&oacute; thể c&oacute; khả năng tăng cường hiệu lực của Androgen hoặc tăng cường qu&aacute; tr&igrave;nh chế tiết hormon Androgen (Trung Dược Học).</p>\r\n\r\n<p>10.5. Nước sắc Ba K&iacute;ch c&oacute; t&aacute;c dụng tương tự như ACTH l&agrave;m cho tuyến ức chuột con bị teo (Trung Dược Học).</p>\r\n\r\n<p>10.6. Nước sắc Ba K&iacute;ch c&oacute; t&aacute;c dụng l&agrave;m tăng co b&oacute;p của chuột v&agrave; hạ huyết &aacute;p (Trung Dược Học).</p>\r\n\r\n<p>10.7. Kh&ocirc;ng c&oacute; độc. LD50&nbsp; của Ba K&iacute;ch được x&aacute;c định tr&ecirc;n chuột nhắt trắng bằng đường uống l&agrave; 193g/kg (Trung Dược Dược l&yacute;, Độc l&yacute; Dữ L&acirc;m S&agrave;ng).</p>\r\n\r\n<p>Rễ Ba k&iacute;ch chiết xuất bằng rượu c&oacute; t&aacute;c dụng gi&aacute;ng &aacute;p huyết; c&oacute; t&aacute;c dụng nhanh đối với c&aacute;c tuyến cơ năng; tăng cường n&atilde;o; chống ngủ ngon d&ugrave;ng Ba k&iacute;ch nhục (Trung Dược Dược l&yacute;, Độc l&yacute; Dữ L&acirc;m S&agrave;ng).</p>\r\n\r\n<p>T&aacute;c dụng đối với hệ nội tiết: Cho chuột v&agrave; chuột nhắt uống Ba k&iacute;ch thi&ecirc;n thấy kh&ocirc;ng c&oacute; t&aacute;c dụng giống như chất Androgen (Trung Dược Học).</p>\r\n\r\n<p>Đối với những bệnh nh&acirc;n nam c&oacute; hoạt động sinh dục kh&ocirc;ng b&igrave;nh thường, Ba K&iacute;ch c&oacute; t&aacute;c dụng l&agrave;m tăng khả năng gioa hợp, đặc biệt đối với những trường hợp giao hợp yếu v&agrave; thưa. Ba K&iacute;ch c&oacute; t&aacute;c dụng tăng cường sức dẻo dai, mặc dầu n&oacute; kh&ocirc;ng l&agrave;m tăng đ&ograve;i hỏi t&igrave;nh dục, kh&ocirc;ng thấy c&oacute; t&aacute;c dụng kiểu Androgen. Tuy kh&ocirc;ng l&agrave;m thay đổi tinh dịch đồ nhưng tr&ecirc;n thực tế c&oacute; t&aacute;c dụng hỗ trợ v&agrave; cải thiện hoạt động sinh dục cũng như điều trị v&ocirc; sinh cho những nam giới c&oacute; trạng th&aacute;i v&ocirc; sinh tương đối v&agrave; suy nhược thể lực. C&ograve;n c&aacute;c trường hợp tinh dịch &iacute;t, tinh tr&ugrave;ng chết nhiều, kh&ocirc;ng c&oacute; tinh tr&ugrave;ng, kh&ocirc;ng xuất tinh khi giao hợp th&igrave; xử dụng Ba K&iacute;ch chưa thấy kết quả (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc ViệtNam).</p>\r\n\r\n<p>Đối với cơ thể những người tuổi gi&agrave;, những bệnh nh&acirc;n kh&ocirc;ng biểu hiện mệt mỏi, ăn k&eacute;m, ngủ &iacute;t, gầy yếu m&agrave; kh&ocirc;ng thấy c&oacute; những yếu tố bệnh l&yacute; g&acirc;y n&ecirc;n v&agrave; 1 số trường hợp c&oacute; đau mỏi c&aacute;c khớp, Ba K&iacute;ch c&oacute; t&aacute;c dụng tăng lực r&otilde; rệt, thể hiện qua những cảmgi&aacute;c chủ quan như đỡ mệt mỏi, ăn ngon, ngủ ngon v&agrave; những dấu hiệu kh&aacute;ch quan như tăng c&acirc;n nặng, tăng cơ lực. C&ograve;n đối với bệnh nh&acirc;n đau mỏi c&aacute;c khớp th&igrave; sau khi d&ugrave;ng Ba K&iacute;ch d&agrave;i ng&agrave;y, c&aacute;c triệu chứng đau mỏi giảm r&otilde; rệt (T&agrave;i Nguy&ecirc;n C&acirc;y Thuốc Việt&nbsp;Nam).</p>\r\n\r\n<p><strong>11. C&ocirc;ng năng:</strong>&nbsp;&Ocirc;n thận, tr&aacute;ng dương, cường tr&aacute;ng c&acirc;n cốt, khử phong thấp</p>\r\n\r\n<p><strong>12. C&ocirc;ng dụng:&nbsp;</strong>Chữa liệt dương, di tinh, phụ nữ kh&oacute; c&oacute; thai, kinh nguyệt chậm, bế kinh, đau lưng mỏi gối...</p>\r\n\r\n<p><strong>13. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>Ba k&iacute;ch dược d&ugrave;ng chữa dương uỷ, phong thấp cước kh&iacute;, g&acirc;n cốt yếu mềm, lưng gối mỏi đau. Trong nh&acirc;n d&acirc;n, Ba k&iacute;ch l&agrave; vị thuốc&nbsp;&nbsp;c&oacute; t&aacute;c dụng bổ tr&iacute; n&atilde;o v&agrave; tinh kh&iacute;, chữa xuất tinh sớm, di mộng tinh, liệt dương, kinh nguyệt chậm hoặc bế kinh, phong thấp, huyết &aacute;p cao.</p>\r\n\r\n<p>Ng&agrave;y d&ugrave;ng 8-16 g, dạng thuốc sắc, cao lỏng, vi&ecirc;n chế từ cao hoặc rượu thuốc. Người ta thường đ&agrave;o củ c&acirc;y mọc hoang về nấu với thịt g&agrave; ăn hoặc ng&acirc;m rượu uống để bồi bổ sức khoẻ.</p>\r\n\r\n<p>Kh&ocirc;ng d&ugrave;ng khi rong kinh, kinh sớm. Người &acirc;m hư qu&aacute; vượng đại tiện t&aacute;o kết kh&ocirc;ng n&ecirc;n d&ugrave;ng.</p>\r\n\r\n<p><strong>14. C&aacute;ch d&ugrave;ng, liều lượng:</strong>&nbsp;Ng&agrave;y 8-16g dưới dạng thuốc sắc hay rượu thuốc. Phối hợp trong c&aacute;c phương thuốc bổ thận.</p>\r\n', '2016-12-01 17:25:59', 0, 0, 1, 1),
(32, 'Chè vằng khô', 98000, 198, 'che-vang-kho-goi.jpg', 1, '<p>Th&agrave;nh phần: 100% ch&egrave; vằng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>&nbsp;C&ocirc;ng dụng:</h2>\r\n\r\n<p>&nbsp;- Thanh nhiệt, ti&ecirc;u độc, lợi mật, lợi thấp, giảm đau nhức xương khớp</p>\r\n\r\n<p>&nbsp;- Chống vi&ecirc;m da, mụn nhọt, r&ocirc;m sẩy, mẩn ngứa, giữ cho da mịn, sạch mụn</p>\r\n\r\n<p>&nbsp;- kh&aacute;ng vi&ecirc;m, gi&uacute;p lợi sữa, chống tắc sữa, vi&ecirc;m tuyến vũ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>&nbsp;Đối tượng sử dụng:</h2>\r\n\r\n<p>&nbsp;-D&ugrave;ng cho mọi đối tượng. Tốt cho phụ nữ sau khi sinh, &iacute;t sữa, tắc sữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Hướng dẫn sử dụng:</h2>\r\n\r\n<p>&nbsp;-Lấy 20 - 30 g c&ugrave;ng 1,5 &ndash; 2l nướcđun s&ocirc;i khoảng 5 ph&uacute;t. Sử dụng l&agrave;m thức uống hằng ng&agrave;y cho gia đ&igrave;nh, c&oacute; thể uống n&oacute;ng hoặc lạnh.</p>\r\n', '2016-12-01 17:32:31', 4, 1, 2, 1),
(33, 'Bột trà xanh matcha Kyoto', 190000, 23, 'bot-tra-xanh-kyoto.jpg', 1, '<p>Tr&agrave; xanh chứa các ch&acirc;́t&nbsp; chống oxy ho&aacute;, chống khuẩn, chống b&eacute;o ph&igrave;, hấp thụ Tannin v&agrave; b&agrave;i thải sắc tố đen trong cơ thể gi&uacute;p da trắng, chống ung thư. Diệp lục trong trà xanh th&uacute;c đẩy sự t&aacute;i sinh của c&aacute;c tổ chức v&agrave; dịch m&aacute;u, c&aacute;c loại đường c&oacute; thể tăng cường khả năng miễn dịch, Cafein gi&uacute;p đầu &oacute;c tỉnh t&aacute;o, c&oacute; t&aacute;c dụng l&agrave;m săn chắc da. Ngo&agrave;i ra n&oacute; c&ograve;n chứa nhiều vitamin, kho&aacute;ng chất, Axit Amin c&oacute; chức năng điều tiết protein, gi&uacute;p da tr&agrave;n trề sức sống.<br />\r\n<br />\r\n<strong>CÁC C&Ocirc;NG THỨC LÀM ĐẸP</strong><br />\r\n<br />\r\n- D&ugrave;ng 1 th&igrave;a c&agrave; ph&ecirc; b&ocirc;̣t trà xanh đun s&ocirc;i với 25ml nước, om trong 40 ph&uacute;t rồi lọc lấy nước trong, để nguội, sau đ&oacute; d&ugrave;ng gạc mềm tẩm nước xoa đều l&ecirc;n mặt v&agrave; cổ trong mỗi buổi s&aacute;ng v&agrave; tối trước khi đi ngủ. C&aacute;ch l&agrave;m n&agrave;y sẽ gi&uacute;p da mặt bạn kh&ocirc;ng bị sạm đen bởi &aacute;nh nắng...<br />\r\n<br />\r\n- Trộn 100g mật ong với 2 th&igrave;a c&agrave; ph&ecirc; nước ch&egrave;, cũng d&ugrave;ng gạc mềm tẩm dung dịch đắp l&ecirc;n mặt từ 10-15 ph&uacute;t, sau đ&oacute; rửa lại bằng nước sạch. L&agrave;m đều đặn 1 lần/ng&agrave;y, bạn sẽ c&oacute; 1 l&agrave;n da mềm mại v&agrave; tươi s&aacute;ng.<br />\r\n<br />\r\n- D&ugrave;ng 1 th&igrave;a canh bột gạo khấy đều với b&ocirc;̣t trà xanh và nước th&agrave;nh 1 hỗn hợp nhuyễn rồi trộn với l&ograve;ng đỏ trứng g&agrave;. Xoa hỗn hợp l&ecirc;n mặt 15-20 ph&uacute;t, sau đ&oacute; rửa bằng nước ấm. D&ugrave;ng 1 lần/tuần, những nếp nhăn của bạn sẽ dần biến mất.<br />\r\n<br />\r\n- Trộn b&ocirc;̣t trà xanh c&ugrave;ng &iacute;t kem tẩy tế b&agrave;o chết cho da mặt hay cổ, duy tr&igrave; một tuần hai lần. Hoạt chất trong tr&agrave; sẽ lấy sạch bụi, kh&aacute;ng khuẩn v&agrave; đem lại cho da sự tươi mới, m&aacute;t mẻ.<br />\r\n<br />\r\n- B&ocirc;̣t trà xanh ho&agrave; c&ugrave;ng kem dưỡng thể rồi cho v&agrave;o tủ lạnh để ngăn m&aacute;t một l&uacute;c. T&aacute;c dụng của hỗn hợp n&agrave;y l&agrave; l&agrave;m m&aacute;t da v&agrave; bổ sung dưỡng chất cho da.<br />\r\n<br />\r\n- Pha tr&agrave; xanh v&agrave;o nước n&oacute;ng, đợi cho hơi n&oacute;ng bốc đều lan toả, bạn gh&eacute; mặt v&agrave;o cốc tr&agrave; đang kh&oacute;i nhẹ nhắm mắt, h&iacute;t thật s&acirc;u hương vị cho tinh thần thư th&aacute;i v&agrave; để hơi tr&agrave; x&ocirc;ng mặt cho gi&atilde;n nở lỗ ch&acirc;n l&ocirc;ng. Sau đ&oacute;, thay bằng hỗn hợp tr&agrave; xanh ho&agrave; với kem dưỡng đ&atilde; để tủ lạnh để b&ocirc;i lại cho se lỗ ch&acirc;n l&ocirc;ng.<br />\r\n- Trộn đều 1 th&igrave;a bột tr&agrave; xanh, 1 th&igrave;a bạch chỉ, 2 th&igrave;a bột đậu xanh, đắp k&iacute;n l&ecirc;n mặt khoảng khoảng 10 &ndash; 15 ph&uacute;t rồi d&ugrave;ng nước lạnh rửa lại sẽ gi&uacute;p l&agrave;m sạch da, dưỡng v&ugrave;ng da bị tổn thương do mụn.<br />\r\n<br />\r\n- Trộn đều 1 th&igrave;a bột tr&agrave; xanh, 1 th&igrave;a bột m&igrave;, 2 th&igrave;a mật ong, sau đ&oacute; rồi b&ocirc;i lớp mỏng l&ecirc;n mặt, ch&uacute; &yacute; cằm v&agrave; cổ c&oacute; thể b&ocirc;i nhiều hơn một ch&uacute;t. Để khoảng 20 &ndash; 30 ph&uacute;t rồi rửa lại bằng nước lạnh. N&ecirc;n b&ocirc;i mặt nạ n&agrave;y l&uacute;c vừa tắm xong l&agrave; tốt hơn cả. Mặt nạ n&agrave;y sẽ gi&uacute;p da bạn tươi s&aacute;ng hơn.<br />\r\n<br />\r\n- Tạo nước hoa hồng: 1 lạng bột tr&agrave; xanh, nửa l&iacute;t rượu nhẹ (dưới 10 độ).<br />\r\n<br />\r\nCho cả hai thứ đ&oacute; v&agrave;o một b&igrave;nh thật sạch, n&uacute;t chặt lại v&agrave; để v&agrave;o chỗ m&aacute;t. Khoảng 1 th&aacute;ng sau lấy ra sử dụng, đảm bảo nước hoa hồng n&agrave;y c&ograve;n hiệu quả hơn so với c&aacute;c mỹ phẩm th&ocirc;ng thường.<br />\r\n&nbsp;</p>\r\n', '2016-12-01 17:34:00', 0, 0, 7, 1),
(34, 'Hầu thủ FUSI', 536000, 200, 'hau thu fusi.jpg', 1, '<p><strong>Hầu Thủ Fusi c&oacute; c&ocirc;ng dụng:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Bồi bổ sức khỏe, tăng cường sức đề kh&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hỗ trợ điều trị vi&ecirc;m lo&eacute;t dạ d&agrave;y, t&aacute; tr&agrave;ng, vi&ecirc;m lo&eacute;t ruột&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&aacute;c bệnh về đường ti&ecirc;u h&oacute;a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hỗ trợ điều trị ung thư dạ d&agrave;y, c&aacute;c ung thư về ti&ecirc;u h&oacute;a&hellip;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;ch d&ugrave;ng</strong>: uống l&uacute;c đ&oacute;i</p>\r\n\r\n<p>- Tăng sức đề kh&aacute;ng, ph&ograve;ng chống bệnh tật: Mỗi lần d&ugrave;ng 2 vi&ecirc;n, ng&agrave;y d&ugrave;ng 3 lần (s&aacute;ng, trưa, tối)</p>\r\n\r\n<p>- Hỗ trợ điều trị bệnh tật liều d&ugrave;ng gấp đ&ocirc;i</p>\r\n\r\n<p>- Trẻ em liều d&ugrave;ng bằng &frac12; người lớn.</p>\r\n\r\n<p><em>Ch&uacute; &yacute;</em>:&nbsp;<strong>Hầu thủ Fusi kh&ocirc;ng c&oacute; t&aacute;c dụng phụ.</strong></p>\r\n', '2016-12-01 17:35:03', 5, 1, 0, 1),
(35, 'Vân chi FUSI', 586000, 200, 'van chi fusi.jpg', 1, '<p><strong>1. V&acirc;n Chi Fusi c&oacute; c&ocirc;ng dụng:</strong></p>\r\n\r\n<p>- Hỗ trợ điều trị vi&ecirc;m gan B.</p>\r\n\r\n<p>- Hạn chế qu&aacute; tr&igrave;nh ph&aacute;t triển của ung thư gan .</p>\r\n\r\n<p>- Hỗ trợ điều trị ung thư đường h&ocirc; hấp, ung thư v&uacute;, ung thư tử cung, buồng trứng...</p>\r\n\r\n<p><strong>2. Nguồn gốc</strong></p>\r\n\r\n<p>V&acirc;n Chi Fusi được thu h&aacute;i v&agrave; b&agrave;o chế từ sợi thể (thể sợi) nấm V&acirc;n chi.</p>\r\n', '2016-12-01 17:35:46', 4, 2, 0, 1),
(36, 'Hồng chi FUSI', 286000, 200, 'hong chi fusi.jpg', 1, '<p><strong>Hồng Chi Fusi c&oacute; c&ocirc;ng dụng:</strong></p>\r\n\r\n<p>- Tăng cường sức khỏe, tăng khả năng miễn dịch của cơ thể.</p>\r\n\r\n<p>- Gi&uacute;p ổn định huyết &aacute;p đối với bệnh nh&acirc;n huyết &aacute;p cao.</p>\r\n\r\n<p>- Gi&uacute;p giảm suy nhược thần kinh.</p>\r\n\r\n<p>- Hỗ trợ hoạt động của tim mạch</p>\r\n\r\n<p>- Hỗ trợ hoạt động của gan</p>\r\n\r\n<p>- Hỗ trợ điều trị ung thư.</p>\r\n\r\n<p><strong>Liều lượng v&agrave; c&aacute;ch d&ugrave;ng</strong></p>\r\n\r\n<p>- D&ugrave;ng nước n&oacute;ng (60-70độ C) để pha uống: b&oacute;c g&oacute;i Hồng chi FUSI đổ v&agrave;o cốc sau đ&oacute; đổ nước v&agrave;o khuấy đều cho tan rồi uống hết.</p>\r\n\r\n<p>- Hiệu quả của Linh chi tương đối chậm, khoảng 2-3 ng&agrave;y mới c&oacute; hiệu quả, c&oacute; trường hợp 10-30 ng&agrave;y nhưng hiệu quả ổn định, khi điều trị một bệnh n&agrave;o đ&oacute; l&agrave;m thuy&ecirc;n giảm c&aacute;c bệnh kh&aacute;c, điều trị một c&aacute;ch tổng thể, n&acirc;ng cao to&agrave;n thể trạng v&agrave; ph&ograve;ng ngừa bệnh.</p>\r\n\r\n<p>- D&ugrave;ng cho người lớn 2 g&oacute;i 1 ng&agrave;y v&agrave; trẻ em dưới 6 tuổi d&ugrave;ng bằng &frac12; người lớn. Trong trường hợp bị suy kiệt hay cần hỗ trợ sức khỏe nhanh th&igrave; d&ugrave;ng gấp đ&ocirc;i.</p>\r\n', '2016-12-01 17:36:34', 0, 0, 0, 1),
(37, 'Trùng thảo FUSI', 986000, 0, 'trung thao fusi.jpg', 1, '<p>Đ&ocirc;ng tr&ugrave;ng hạ thảo l&agrave; một dược liệu qu&yacute; hiếm, m&agrave; v&agrave;o m&ugrave;a đ&ocirc;ng l&agrave; con s&acirc;u nhưng đến m&ugrave;a hạ th&igrave; th&agrave;nh c&acirc;y cỏ.Tại Trung Quốc, đ&ocirc;ng tr&ugrave;ng hạ thảo thường gặp ở v&ugrave;ng rừng ẩm ướt tại c&aacute;c tỉnh Tứ Xuy&ecirc;n, V&acirc;n Nam, T&acirc;y Khang, T&acirc;y Tạng.</p>\r\n\r\n<p>Đ&ocirc;ng tr&ugrave;ng hạ thảo chứa protein (25 - 32%) một loại acid đặc biệt (acid cordycepic 1,3,4,5 - tetra - oxyhexahydrobenzoi) 7% v&agrave; cordycepin.</p>\r\n\r\n<p>Theo đ&ocirc;ng y, đ&ocirc;ng tr&ugrave;ng hạ thảo vị ngọt, t&iacute;nh &ocirc;n, v&agrave;o hai kinh phế v&agrave; thận. N&oacute; c&oacute; t&aacute;c dụng &iacute;ch phế thận, cầm m&aacute;u, ho&aacute; đờm, d&ugrave;ng chữa hư lao sinh ho, ho ra m&aacute;u, yếu sinh l&yacute;. Ngo&agrave;i ra Đ&ocirc;ng tr&ugrave;ng hạ thảo c&oacute; thể d&ugrave;ng để điều trị bệnh vi&ecirc;m phế quản m&atilde;n tĩnh v&agrave; hen phế quản rất tốt.</p>\r\n\r\n<p>Tr&ugrave;ng thảo Fusi được thu h&aacute;i v&agrave; b&agrave;o chế từ 100% sợi thể (thể sợi) đ&ocirc;ng tr&ugrave;ng hạ thảo bằng phương ph&aacute;p sinh học hiện đại, m&agrave; kh&ocirc;ng c&oacute; th&ecirc;m bất cứ một t&aacute; dược n&agrave;o kh&aacute;c. Sợi thể Đ&ocirc;ng tr&ugrave;ng hạ thảo chứa tất cả c&aacute;c axitamin, vitamin B1, B2, B12, E v&agrave; K, một loạt c&aacute;c loại đường bao gồm mono-di v&agrave; Si, Ni,&hellip;v&agrave; rất nhiều Polysaccharids(rất đa dạng v&agrave; phức tạp), Protein, Sterol, Nucleosides v&agrave; c&aacute;c chất v&ocirc; cơ: K, Na, Ca. Ngo&agrave;i ra, sợi thể Đ&ocirc;ng tr&ugrave;ng hạ thảo c&ograve;n chứa 28 axit b&eacute;o no v&agrave; chưa no trong đ&oacute; đặc biệt c&oacute; c&aacute;c axit b&eacute;o chưa no c&oacute; t&aacute;c dụng chống oxy h&oacute;a như acidlinoleic&hellip;chiếm 82%.</p>\r\n', '2016-12-01 17:37:24', 0, 0, 11, 1),
(38, 'Hà thủ ô', 38000, 14, 'ha thu o.jpg', 1, '<p>H&atilde;ng sản xuất : Nguyễn Th&aacute;i Trang<br />\r\nC&ocirc;ng dụng : Tr&agrave; được chế biến từ củ H&Agrave; THỦ &Ocirc;, c&ograve;n c&oacute; t&ecirc;n l&agrave; DẠ GIAO ĐẰNG ( v&igrave; ban đ&ecirc;m hai th&acirc;n d&acirc;y quấn lại v&agrave;o nhau ) . T&ecirc;n khoa học : Polygonum Multiflorum .<br />\r\n- H&agrave; Thủ &Ocirc; l&agrave; một loại dựoc liệu qu&yacute; sống thi&ecirc;n nhi&ecirc;n, trong d&acirc;n gian d&ugrave;ng l&agrave;m thuốc bổ m&aacute;u, trị thần kinh suy nhược, kh&oacute; ngủ. D&ugrave;ng l&acirc;u bổ gan thận , dưỡng huyết , &iacute;ch tinh, mạnh g&acirc;n cốt, sống l&acirc;u.L&agrave;m đen t&oacute;c, tr&aacute;nh rụng t&oacute;c, bồi bổ cơ thể, đem lại thanh xu&acirc;n cho mọi người</p>\r\n', '2016-12-01 17:39:40', 4, 1, 16, 1);
INSERT INTO `products` (`pid`, `name`, `price`, `quantity`, `image`, `packing_method`, `description`, `created_at`, `rating`, `rating_times`, `sold`, `oeid`) VALUES
(39, 'Trà linh chi', 133000, 166, 'tra linh chi.jpg', 1, '<p>Tr&agrave; linh chi c&oacute; ưu điểm l&agrave; điều chế đơn giản, sử dụng thuận tiện, kh&ocirc;ng phải đun nấu cầu kỳ như thuốc sắc. Người ta thường chọn mua loại nấm to, l&agrave;nh lặn, dầy dặn v&agrave; c&ograve;n nguy&ecirc;n t&aacute;n.<br />\r\n<br />\r\nSau c&ocirc;ng đoạn l&agrave;m sạch, d&ugrave;ng dao th&aacute;i vụn hoặc th&aacute;i th&agrave;nh l&aacute;t mỏng, c&agrave;ng vụn c&agrave;ng mỏng th&igrave; c&agrave;ng tốt v&igrave; như vậy khi h&atilde;m với nước s&ocirc;i mới chiết xuất được tối đa hoạt chất.<br />\r\n<br />\r\nCuối c&ugrave;ng đem sấy hoặc phơi thật kh&ocirc; rồi đựng trong lọ k&iacute;n d&ugrave;ng dần. Cũng c&oacute; thể d&ugrave;ng m&aacute;y t&aacute;n th&agrave;nh dạng mịn như b&ocirc;ng, c&aacute;ch n&agrave;y gi&uacute;p người ta sau khi h&atilde;m vừa uống được nước vừa ăn cả b&atilde; một c&aacute;ch dễ d&agrave;ng v&agrave; tiết kiệm.<br />\r\nH&atilde;ng sản xuất Ganoderma<br />\r\nXuất xứ: Bắc Triều Ti&ecirc;n</p>\r\n', '2016-12-01 17:40:37', 5, 1, 34, 1),
(40, 'Hồng sâm', 200000, 24, 'tra-hong-sam.jpg', 2, '<p>Quy c&aacute;ch<br />\r\n-T&ecirc;n gọi: Tr&agrave; Hồng S&acirc;m, Korean Red Ginseng Tea<br />\r\n- Trọng lượng: 300gr / 100 g&oacute;i<br />\r\n- Dạng th&agrave;nh phẩm: hộp gỗ<br />\r\n<br />\r\n<strong>C&ocirc;ng Dụng:</strong><br />\r\n-Ph&ograve;ng chống ung thư, tốt cho gan, thận, điều h&ograve;a huyết &aacute;p vừa c&oacute; khả năng dự ph&ograve;ng t&iacute;ch cực c&aacute;c biến chứng tim mạch thường c&oacute; tr&ecirc;n bệnh nh&acirc;n bị bệnh tiểu đường, đặc biệt l&agrave; t&igrave;nh trạng vi&ecirc;m tắc động tĩnh mạch.<br />\r\n-Bổ gan, thận, giải độc.<br />\r\n-Vi&ecirc;m gan cấp v&agrave; m&atilde;n t&iacute;nh, gan nhiễm mỡ.<br />\r\n-Vi&ecirc;m gan si&ecirc;u vi A+B c&aacute;c loại.<br />\r\n<br />\r\n<strong>Xuất xứ</strong>&nbsp;<strong>:</strong>&nbsp;H&agrave;n Quốc</p>\r\n', '2016-12-01 17:42:08', 5, 2, 26, 1);

